TS.raw_templates['admin_invite_channel_picker'] = "{{#if_equal invite_type compare='full'}}\n\t<p id=\"default_channels_note\" class=\"small align_center no_bottom_margin {{#if initial_channel_id}}hidden{{\/if}}\">\n\t\tNew <strong>full members<\/strong> will automatically join\n\t\t{{#each default_channels}}\n\t\t\t{{#if @last}}{{#unless @first}}and {{\/unless}}{{\/if}}#<strong>{{this.name}}<\/strong>{{#unless @last}}{{#if_gt ..\/default_channels.length compare=2}},{{\/if_gt}}{{\/unless}}\n\t\t{{\/each}}\n\t\t{{#if is_admin}}\n\t\t\t<a onclick=\"$('#default_channels_note').hide(); $('#default_channels').show();\" class=\"small_left_margin subtle_silver underline\" data-clog-click=\"true\" data-clog-event=\"INVITEMODAL_ACTION\" data-clog-params=\"action=edit_channels,trigger=click_cta_edit_channels\">edit \/ add<\/a>\n\t\t{{\/if}}\n\t<\/p>\n\t<p id=\"default_channels\" {{#if initial_channel_id}}{{else}}style=\"display: none; overflow: visible !important;\"{{\/if}}>\n\t\t<select multiple=\"multiple\" id=\"defaultchannelsmulti\" name=\"defaultchannels[]\" class=\"hidden\">\n\t\t\t{{#each channels}}\n\t\t\t\t{{#unless this.is_general}}\n\t\t\t\t\t<option value=\"{{this.id}}\" {{#if this.is_default}}selected{{\/if}} {{#if_equal this.id compare=..\/initial_channel_id}}selected{{\/if_equal}}>#{{this.name}}<\/option>\n\t\t\t\t{{\/unless}}\n\t\t\t{{\/each}}\n\t\t\t{{#each groups}}\n\t\t\t\t<option value=\"{{this.id}}\">{{this.name}}<\/option>\n\t\t\t{{\/each}}\n\t\t<\/select>\n\t\t<span class=\"small align_center\">\n\t\t\tNew <strong>full members<\/strong> will automatically join these channels and <strong>#{{general_name}}<\/strong> <a href=\"\/admin\/settings#default_channels\" target=\"admin_settings\" class=\"small_left_margin subtle_silver underline\">change defaults<\/a>\n\t\t<\/span>\n\t<\/p>\n{{\/if_equal}}\n\n{{#if_equal invite_type compare='restricted'}}\n\t<p class=\"small align_center small_bottom_margin\">New <strong>{{raLabel 'restricted accounts'}}<\/strong>