TS.raw_templates['admin_invite_summary'] = "<h1 class=\"light align_center\">\n\t{{#if error_invites}}\n\t\t{{#if success_invites}}\n\t\t\tThat was only a partial success.\n\t\t{{else}}\n\t\t\tThat didn't work!\n\t\t{{\/if}}\n\t{{else}}\n\t\tYour invitation{{#if_gt success_invites.length compare=1}}s have{{else}} has{{\/if_gt}} been sent!\n\t{{\/if}}\n<\/h1>\n\n<p class=\"align_center large_bottom_margin\">\n\t{{#if error_invites}}\n\t\t{{#if success_invites}}\n\t\t\tYou've invited {{{success_invites_html}}}, but\n\t\t{{\/if}}\n\t\t<span class=\"bold\">{{error_invites.length}} {{pluralize error_invites.length 'invitation' 'invitations'}} didn't send<\/span>. Review the errors below.\n\t{{else}}\n\t\tYou've invited {{{success_invites_html}}} to {{team_name}}.\n\t{{\/if}}\n<\/p>\n\n{{#if success_invites}}\n\t{{#feature flag=\"feature_name_tagging_client\"}}\n\t\t<table class=\"addresses large_bottom_margin align_left full_width\">\n\t\t\t<tr>\n\t\t\t\t<th class=\"span_1_of_2\">Email Address<\/th>\n\t\t\t\t<th class=\"span_1_of_2\">Full Name<\/th>\n\t\t\t<\/tr>\n\t\t\t{{#each success_invites}}\n\t\t\t\t<tr>\n\t\t\t\t\t<td class=\"align_top\">{{this.email}}<\/td>\n\t\t\t\t\t<td>{{this.full_name}}<\/td>\n\t\t\t\t<\/tr>\n\t\t\t{{\/each}}\n\t\t<\/table>\n\t{{else}}\n\t\t<table class=\"addresses large_bottom_margin align_left full_width\">\n\t\t\t<tr>\n\t\t\t\t<th class=\"span_1_of_2\">Email Address<\/th>\n\t\t\t\t<th class=\"span_1_of_2\">Name<\/th>\n\t\t\t<\/tr>\n\t\t\t{{#each success_invites}}\n\t\t\t\t<tr>\n\t\t\t\t\t<td class=\"align_top\">{{this.email}}<\/td>\n\t\t\t\t\t<td>{{this.first_name}} {{this.last_name}}<\/td>\n\t\t\t\t<\/tr>\n\t\t\t{{\/each}}\n\t\t<\/table>\n\t{{\/feature}}\n{{\/if}}\n\n{{#if error_invites}}\n\t<table class=\"addresses error large_bottom_margin align_left full_width\">\n\t\t<tr>\n\t\t\t<th class=\"span_1_of_2\">Email Address<\/th>\n\t\t\t<th class=\"span_1_of_2\">What went wrong<\/th>\n\t\t<\/tr>\n\t\t{{#each error_invites}}\n\t\t\t<tr>\n\t\t\t\t<td class=\"align_top\">{{this.email}}<\/td>\n\t\t\t\t<td>{{safeString this.error_msg}}<\/td>\n\t\t\t<\/tr>\n\t\t{{\/each}}\n\t<\/table>\n{{\/if}}\n\n{{#if custom_message}}\n\t<table class=\"large_bottom_margin align_left full_width\">\n\t\t<tr>\n\t\t\t<th class=\"span_1_of_1\">Custom Message<\/th>\n\t\t<\/tr>\n\t\t<tr>\n\t\t\t<td class=\"align_top break_word\">{{custom_message}}<\/td>\n\t\t<\/tr>\n\t<\/table>\n{{\/if}}\n\n{{#if domains}}\n\t<div class=\"alert alert_success no_bottom_margin clearfix\">\n\t\t<i class=\"ts_icon ts_icon_check_circle_o\"><\/i>\n\t\t<p class=\"value_proposition\">You can set <strong>{{team_name}}<\/strong> to allow automatic sign up from anyone with a confirmed email address.<\/p>\n\t\t{{#if paid_team}}<p class=\"value_proposition\">Slack will automatically charge or credit your account when active members are added to or removed from your team.<\/p>{{\/if}}\n\t\t<p class=\"top_margin\">\n\t\t\t<span class=\"col span_3_of_4 no_right_padding small_bottom_margin\">\n\t\t\t\t<input type=\"text\" id=\"invite_signup_domains\" class=\"no_bottom_margin small\" value=\"{{domains}}\" \/>\n\t\t\t<\/span>\n\t\t\t<button class=\"btn ladda-button col span_1_of_4\" data-style=\"expand-right\" data-action=\"add_signup_domains\">Set Up<\/button>\n\t\t\t<span class=\"input_note col span_1_of_1 no_margin\">To allow email addresses from multiple domains, separate them with commas.<\/span>\n\t\t<\/p>\n\t<\/div>\n{{\/if}}\n\n<p class=\"large_top_padding\">\n\t{{#if is_admin}}\n\t\t<a class=\"btn btn_outline col span_1_of_3\" href=\"\/admin\/invites\" {{#isClient}}target=\"admin_invites\"{{\/isClient}}>View Pending Invites<\/a>\n\t{{\/if}}\n\t{{#if error_invites}}\n\t\t<button class=\"btn btn_outline col {{#if is_admin}}span_1_of_3{{else}}span_1_of_2{{\/if}}\" data-action=\"admin_invites_try_again\">Try Again<\/button>\n\t{{else}}\n\t\t<button class=\"btn btn_outline col {{#if is_admin}}span_1_of_3{{else}}span_1_of_2{{\/if}}\" data-action=\"admin_invites_reset\">Invite More People<\/button>\n\t{{\/if}}\n\t<button class=\"btn btn_outline col {{#if is_admin}}span_1_of_3{{else}}span_1_of_2{{\/if}} dialog_cancel\">Done<\/button>\n<\/p>";
TS.raw_templates['admin_invite_switcher'] = "<hr id=\"admin_invites_header_divider\" class=\"hide_on_mobile\" \/>\n<div class=\"admin_invites_account_type_option {{#if hide_full_member_option}}disabled{{\/if}} {{#if team_in_org}}no_hover{{\/if}}\" data-action=\"switch_type\" data-account-type=\"full\">\n\t<{{#isClient}}h2{{else}}h3{{\/isClient}} class=\"normal small_bottom_margin\">Full Members<\/{{#isClient}}h2{{else}}h3{{\/isClient}}>\n\t<p>\n\t{{#if team_in_org}}\n\tOnly Org Admins can add full team members.\n\t{{else}}\n\tFull Members can access messages and files in any public channel and access the full team directory.\n\t{{\/if}}\n\t<\/p>\n\t{{#unless hide_full_member_option}}\n\t\t<i class=\"option_arrow ts_icon ts_icon_arrow_right ts_icon_inherit\"><\/i>\n\t{{else}}\n\t\t{{#unless team_in_org}}\n\t\t<div class=\"account_type_disabled_hover\">\n\t\t\t<p class=\"bottom_margin\">Full members can sign up at <strong>{{team_signup_url}}<\/strong><\/p>\n\t\t\t<a href=\"\/admin\/auth\/saml\" {{#isClient}}target=\"auth\"{{\/isClient}} class=\"btn btn_outline\">Change SSO Settings<\/a>\n\t\t<\/div>\n\t\t{{\/unless}}\n\t{{\/unless}}\n<\/div>\n<div class=\"admin_invites_account_type_option\" data-action=\"switch_type\" data-account-type=\"restricted\">\n\t<{{#isClient}}h2{{else}}h3{{\/isClient}} class=\"normal small_bottom_margin\">{{raLabel 'Restricted Accounts'}}<\/{{#isClient}}h2{{else}}h3{{\/isClient}}>\n\t<p>{{raLabel 'Restricted Accounts'}} see a partial team directory and can only access messages and files from selected channels.<\/p>\n\t<i class=\"option_arrow ts_icon ts_icon_arrow_right ts_icon_inherit\"><\/i>\n<\/div>\n<div class=\"admin_invites_account_type_option {{#unless can_add_ura}}disabled{{\/unless}}\" data-action=\"switch_type\" data-account-type=\"ultra_restricted\">\n\t<{{#isClient}}h2{{else}}h3{{\/isClient}} class=\"normal small_bottom_margin\">Single-Channel Guests<\/{{#isClient}}h2{{else}}h3{{\/isClient}}>\n\t<p>Single-Channel Guests can only access messages and files in a single channel. This account type is free. <a href=\"https:\/\/slack.zendesk.com\/hc\/en-us\/articles\/202518103-Restricted-account-and-single-channel-guest-FAQ\" target=\"zendesk\" class=\"subtle_silver underline\">Learn more<\/a><\/p>\n\t{{#unless can_add_ura}}\n\t\t<div class=\"account_type_disabled_hover\">\n\t\t\t<p class=\"bottom_margin\">You've reached your team limit for <strong>Single-Channel Guests<\/strong>.<\/p>\n\t\t\t<a href=\"https:\/\/slack.zendesk.com\/hc\/en-us\/articles\/202518103-Restricted-account-and-single-channel-guest-FAQ\" target=\"zendesk\" class=\"btn btn_outline\">Learn More<\/a>\n\t\t<\/div>\n\t{{\/unless}}\n\t<i class=\"option_arrow ts_icon ts_icon_arrow_right ts_icon_inherit\"><\/i>\n<\/div>\n{{#currentUserIsAdmin}}\n\t<p class=\"align_center large_top_margin\">\n\t\t<a href=\"\/admin\/invites\" target=\"{{newWindowName}}\">See pending and accepted invites for your team<\/a>\n\t<\/p>\n{{\/currentUserIsAdmin}}";
TS.raw_templates['admin_restricted_info'] = "\t<div class=\"restricted_info\">\n\t\t\n\t\t<p class=\"large_bottom_margin emphasized align_center\">Give limited access to your Slack instance to contractors, clients, or friends.<\/p>\n\t\t\n\t\t<div class=\"account_type\">\n\t\t\t<div class=\"restricted_account_icon\"><\/div>\n\t\t\t<h3 class=\"no_bottom_margin\">{{raLabel 'Restricted Accounts'}}<\/h3>\n\t\t\t<p class=\"no_bottom_margin\">Can only access channels that they are invited to join.<\/p>\n\t\t\t{{#if paid_team}}\n\t\t\t\t<p>\n\t\t\t\t\t<a data-action=\"admin_invites_modal\" data-account-type=\"restricted\"><i class=\"ts_icon ts_icon_share_email\"><\/i> &nbsp;Invite new {{raLabel 'restricted accounts'}}<\/a>\n\t\t\t\t<\/p>\n\t\t\t{{\/if}}\n\t\t<\/div>\n\t\t<div class=\"account_type\">\n\t\t\t<div class=\"guest_account_icon\"><\/div>\n\t\t\t<h3 class=\"no_bottom_margin\">Single-channel Guests<\/h3>\n\t\t\t<p class=\"no_bottom_margin\">A free account with access to a single channel.<\/p>\n\t\t\t{{#if paid_team}}\n\t\t\t\t<p>\n\t\t\t\t\t<a data-action=\"admin_invites_modal\" data-account-type=\"ultra_restricted\"><i class=\"ts_icon ts_icon_share_email\"><\/i> &nbsp;Invite new single-channel guests<\/a>\n\t\t\t\t<\/p>\n\t\t\t{{\/if}}\n\t\t<\/div>\n\t\t\n\t\t{{#unless paid_team}}\n\t\t\t<div class=\"admin_banner\">\n\t\t\t\t<h3>Both available on our Standard plan.<\/h3>\n\t\t\t\t<p class=\"no_bottom_margin\">Learn more about your <a href=\"\/admin\/billing\">upgrade options<\/a>.<\/p>\n\t\t\t<\/div>\n\t\t{{\/unless}}\n\t\t\n\t<\/div>\t\n";
TS.raw_templates['admin_restricted_info_sso'] = "\t<div class=\"restricted_info_sso\">\n\t\t{{#if enterprise_id}}\n\t\t<p class=\"emphasized align_center\">Need to add a new guest to {{currentTeamName}}? Please contact an Org Admin for assistance provisioning a new guest and assigning them to the team.<\/p>\n\t\t{{else}}\n\t\t<p class=\"emphasized align_center\">Because you have SSO enabled and required for all users, you do not need to invite users to your team. If they have an account with your SSO provider, they can sign in to your Slack team.<\/p>\n\n\t\t<p class=\"no_bottom_margin align_center\"><a href=\"\/admin\/auth\" class=\"btn\">Manage Authentication Settings<\/a><\/p>\n\t\t{{\/if}}\n\t<\/div>";
TS.raw_templates['admin_restrict_account'] = "\t<div class=\"col span_3_of_6\">\n\t\n\t\t<h4>\n\t\t{{#if enabling}}Enable a {{raLabel 'restricted account'}}{{else}}Convert to Guest{{\/if}}\n\t\t<\/h4>\n\n\t\t<p class=\"small_bottom_margin\">\n\t\t\tTeam members you\n\t\t\t{{#if enabling}} enable with a {{raLabel 'Restricted Account'}}{{else}} convert to {{raLabel 'Restricted Accounts'}}{{\/if}}\n\t\t\t will have the following capabilities:\n\t\t<\/p>\n\t\t<ul>\n\t\t\t<li>participate in the channels they are invited to join<\/li>\n\t\t\t<li>exchange direct messages with others in the channels to which they belong<\/li>\n\t\t<\/ul>\n\t\t<p class=\"small_bottom_margin\">They will <strong>not<\/strong> be able to:<\/p>\n\t\t<ul>\n\t\t\t<li>create new channels<\/li>\n\t\t\t<li>see files or messages in channels they don't belong to<\/li>\n\t\t<\/ul>\n\t\t<p class=\"large_bottom_margin\">Single-Channel Guests only have access to one channel.<\/p>\n\t\t\n\t\t<h4>Billing<\/h4>\n\t\t<p class=\"large_bottom_margin\">{{raLabel 'Restricted Accounts'}} count as paid users when they are active. Single-Channel Guests are free.<\/p>\n\t\t\n\t<\/div>\n\n\t<div class=\"col span_3_of_6 invite_form_column\">\n\n\t\t<div id=\"step1\" class=\"align_center\">\n\n\t\t\t{{{makeMemberPreviewLinkImage member.id 192}}}\n\t\n\t\t\t<h3 class=\"top_margin\">\n\t\t\t\t{{#if enabling}}\n\t\t\t\t\tEnable {{#if member.profile.real_name}}{{member.profile.real_name}}{{else}}{{member.name}}{{\/if}} with:\n\t\t\t\t{{else}}\n\t\t\t\t\tConvert {{#if member.profile.real_name}}{{member.profile.real_name}}{{else}}{{member.name}}{{\/if}} to:\n\t\t\t\t{{\/if}}\n\t\t\t<\/h3>\n\t\n\t\t\t<a class=\"restriction_option\" id=\"restricted_user\">\n\t\t\t\t<i class=\"presence large active ra\"><\/i> A {{raLabel 'restricted account'}} <i class=\"ts_icon ts_icon_arrow_right\"><\/i>\n\t\t\t<\/a>\n\t\t\t<a class=\"restriction_option\" id=\"guest_user\">\n\t\t\t\t<i class=\"presence large active ura\"><\/i> A single-channel guest <i class=\"ts_icon ts_icon_arrow_right\"><\/i>\n\t\t\t<\/a>\n\t\t\n\t\t\t<p class=\"subtle_silver large_top_margin\">or <a class=\"cancel_admin_restrict_workflow subtle_silver\">return to Team Administration<\/a><\/p>\n\t\t<\/div>\n\t\n\t\t<div id=\"step2_restricted\" class=\"hidden align_center\">\n\n\t\t\t{{{makeMemberPreviewLinkImage member.id 192}}}\n\t\n\t\t\t<h3 class=\"top_margin\">\n\t\t\t\t{{#if enabling}}Enable{{else}}Convert{{\/if}} {{#if member.profile.real_name}}{{member.profile.real_name}}{{else}}{{member.name}}{{\/if}} {{#if enabling}}as{{else}}to{{\/if}} a {{raLabel 'Restricted Account'}}\n\t\t\t<\/h3>\n\n\t\t\t<p>\n\t\t\t\t{{#if show_ra_channel_picker}}\n\t\t\t\t\tYou must pick one channel for this {{raLabel 'Restricted Account'}} to join.\n\t\t\t\t\t(You can set additional channels in the next step.)\n\t\t\t\t{{else}}\n\t\t\t\t\tYou can set the channels for this {{raLabel 'restricted account'}} in the next step.\n\t\t\t\t{{\/if}}\n\t\t\t\t{{#if enabling}}{{else}}<p>This change will take effect immediately.<\/p>{{\/if}}\n\t\t\t<\/p>\n\n\t\t\t{{#if show_ra_channel_picker}}\n\t\t\t\t<p>\n\t\t\t\t\t<label class=\"select small\">\n\t\t\t\t\t\t<select id=\"restricted_channel_picker\" class=\"small\">\n\t\t\t\t\t\t\t{{#if channels}}\n\t\t\t\t\t\t\t\t<optgroup label=\"Channels\">\n\t\t\t\t\t\t\t\t\t{{#each channels}}\n\t\t\t\t\t\t\t\t\t\t<option value=\"{{this.id}}\">#{{this.name}}<\/option>\n\t\t\t\t\t\t\t\t\t{{\/each}}\n\t\t\t\t\t\t\t\t<\/optgroup>\n\t\t\t\t\t\t\t{{\/if}}\n\t\t\t\t\t\t\t{{#if groups}}\n\t\t\t\t\t\t\t\t<optgroup label=\"Private Channels\">\n\t\t\t\t\t\t\t\t\t{{#each groups}}\n\t\t\t\t\t\t\t\t\t\t<option value=\"{{this.id}}\"><i class=\"ts_icon ts_icon_lock_o\"><\/i> {{this.name}}<\/option>\n\t\t\t\t\t\t\t\t\t{{\/each}}\n\t\t\t\t\t\t\t\t<\/optgroup>\n\t\t\t\t\t\t\t{{\/if}}\n\t\t\t\t\t\t<\/select>\n\t\t\t\t\t<\/label>\n\t\t\t\t<\/p>\n\t\t\t{{\/if}}\n\n\t\t\t<p class=\"error_message moscow_red hidden\">Oops! There was an error performing your action.<\/p>\n\t\t\t<p>\n\t\t\t\t<a class=\"btn api_set_restricted btn_success\">Confirm<\/a>\n\t\t\t\t<a class=\"cancel_admin_restrict_workflow btn btn_outline small_left_margin\">Cancel<\/a>\n\t\t\t<\/p>\n\n\t\t<\/div>\n\n\t\t<div id=\"step2_guest\" class=\"hidden align_center\">\n\n\t\t\t{{{makeMemberPreviewLinkImage member.id 192}}}\n\t\n\t\t\t<h3 class=\"top_margin\">{{#if enabling}}Enable{{else}}Convert{{\/if}} {{#if member.profile.real_name}}{{member.profile.real_name}}{{else}}{{member.name}}{{\/if}} {{#if enabling}}as{{else}}to{{\/if}} a Single-Channel Guest<\/h3>\n\t\n\t\t\t<p class=\"no_bottom_margin\">With access to this channel:<\/p>\n\n\t\t\t<p>\n\t\t\t\t<label class=\"select small\">\n\t\t\t\t\t<select id=\"ultra_restricted_channel_picker\" class=\"small\">\n\t\t\t\t\t\t{{#if channels}}\n\t\t\t\t\t\t\t<optgroup label=\"Channels\">\n\t\t\t\t\t\t\t\t{{#each channels}}\n\t\t\t\t\t\t\t\t\t<option value=\"{{this.id}}\">#{{this.name}}<\/option>\n\t\t\t\t\t\t\t\t{{\/each}}\n\t\t\t\t\t\t\t<\/optgroup>\n\t\t\t\t\t\t{{\/if}}\n\t\t\t\t\t\t{{#if groups}}\n\t\t\t\t\t\t\t<optgroup label=\"Private Channels\">\n\t\t\t\t\t\t\t\t{{#each groups}}\n\t\t\t\t\t\t\t\t\t<option value=\"{{this.id}}\"><i class=\"ts_icon ts_icon_lock_o\"><\/i> {{this.name}}<\/option>\n\t\t\t\t\t\t\t\t{{\/each}}\n\t\t\t\t\t\t\t<\/optgroup>\n\t\t\t\t\t\t{{\/if}}\n\t\t\t\t\t<\/select>\n\t\t\t\t<\/label>\n\t\t\t<\/p>\n\n\t\t\t{{#if enabling}}{{else}}<p>This change will take effect immediately.<\/p>{{\/if}}\n\n\t\t\t<p class=\"error_message moscow_red hidden\">Oops! There was an error performing your action.<\/p>\n\t\t\n\t\t\t<p id=\"convert_to_ura_confirmation\">\n\t\t\t\t<a class=\"btn api_set_ultra_restricted btn_success\">Confirm<\/a>\n\t\t\t\t<a class=\"cancel_admin_restrict_workflow btn btn_outline small_left_margin\">Cancel<\/a>\n\t\t\t<\/p>\n\n\t\t<\/div>\n\t\t\n\t<\/div>\n\t\n";
TS.raw_templates['account_notifications_channel_overrides'] = "\t<table>\n\t\t<tr class=\"hide_when_no_overrides\">\n\t\t\t<td><\/td>\n\t\t\t<td class=\"align_center extra_right_border\" colspan=\"3\"><i class=\"ts_icon ts_icon_laptop small_right_margin\"><\/i><strong>Desktop<\/strong><\/td>\n\t\t\t<td class=\"align_center extra_left_border\" colspan=\"3\"><i class=\"ts_icon ts_icon_mobile small_right_margin\" style=\"font-size: 1.4rem\"><\/i><strong>Mobile<\/strong><\/td>\n\t\t\t<td><\/td>\n\t\t<\/tr>\n\t\t<tr class=\"hide_when_no_overrides\">\n\t\t\t<th><\/th>\n\t\t\t<th>Everything<\/td>\n\t\t\t<th>Mentions<\/th>\n\t\t\t<th class=\"extra_right_border right_padding\">Nothing<\/th>\n\t\t\t<th class=\"extra_left_border\">Everything<\/th>\n\t\t\t<th>Mentions<\/th>\n\t\t\t<th>Nothing<\/th>\n\t\t\t<th class=\"revert_cell_width\"><\/th>\n\t\t<\/tr>\n\t\t<tr id=\"add_override_channel_picker_row\" class=\"hidden\">\n\t\t\t<td colspan=\"8\"><a id=\"add_override_channel_picker\"><i class=\"ts_icon ts_icon_plus_circle ts_icon_inherit float_left small_right_margin\"><\/i> Add a custom setting for a channel<\/a><\/td>\n\t\t<\/tr>\n\t<\/table>\n";
TS.raw_templates['account_notifications_channel_overrides_row'] = "\t<tr id=\"channel_override_row_{{channel.id}}\" class=\"channel_override_row {{#if channel.is_muted}}muted{{\/if}}\">\n\t\t<td class=\"channel_details\">\n\t\t\t<span class=\"no_wrap\">\n\t\t\t\t<i class=\"ts_icon ts_icon_bell_slash mute_icon\"><\/i>\n\t\t\t\t{{~#if channel.is_group~}}\n\t\t\t\t\t{{~#if channel.is_mpim~}}\n\t\t\t\t\t\t<i class=\"ts_icon ts_icon_multiparty_dm_{{mpimMemberCount channel.channel}}\"><\/i>\n\t\t\t\t\t{{~else~}}\n\t\t\t\t\t\t<i class=\"ts_icon ts_icon_lock_o group_lock_prefix\"><\/i>\n\t\t\t\t\t{{~\/if~}}\n\t\t\t\t{{~\/if~}}\n\t\t\t\t{{~#if channel.is_channel~}}\n\t\t\t\t\t<span class=\"channel_hash_prefix\">#<\/span>\n\t\t\t\t{{~\/if~}}\n\t\t\t\t<strong>{{channel.name}}<\/strong>\n\t\t\t\t<span class=\"pref_saved inline_block small_left_margin\"><i class=\"ts_icon ts_icon_check_circle_o\"><\/i> Saved<\/span>\n\t\t\t<\/span>\n\n\t\t\t<label class=\"checkbox no_margin normal\">\n\t\t\t\t<input id=\"muting_cb_{{channel.id}}\" {{#if channel.is_muted}}checked=\"checked\"{{\/if}} type=\"checkbox\" data-id=\"{{channel.id}}\" data-pref=\"muting\" class=\"small_right_margin\">\n\t\t\t\tMute this {{#if channel.is_mpim}}conversation{{else}}{{#if channel.is_group}}private channel{{else}}channel{{\/if}}{{\/if}}\n\t\t\t<\/label>\n\n\t\t\t<label id=\"at_channel_suppressed_{{channel.id}}\" class=\"hidden checkbox no_margin normal {{#if channel.is_muted}}disabled{{\/if}}\">\n\t\t\t\t<input id=\"at_channel_suppressed_cb_{{channel.id}}\" checked=\"checked\" {{#if channel.is_muted}}disabled{{\/if}} type=\"checkbox\" data-id=\"{{channel.id}}\" data-pref=\"at_channel_suppression\" class=\"small_right_margin\"> Suppress <strong>{{#if channel.is_group}}@channel{{else}}{{#if channel.is_general}}@everyone{{else}}@channel{{\/if}}{{\/if}}<\/strong> mentions <span id=\"at_channel_suppressed_mobile_qualifier_{{channel.id}}\">(on mobile)<\/span><span id=\"at_channel_suppressed_desktop_qualifier_{{channel.id}}\">(on desktop)<\/span><span id=\"at_channel_suppressed_disabled_explain_{{channel.id}}\" class=\"hidden\"> (disabled due to <strong id=\"at_channel_suppressed_disabled_explain_tip_link_{{channel.id}}\" class=\"{{#if channel.is_muted}}ts_tip_hide {{\/if}}ts_tip ts_tip_top ts_tip_multiline ts_tip_float\">team settings<span class=\"ts_tip_tip\"><span class=\"ts_tip_multiline_inner\"><\/span><\/span><\/strong>)<\/span>\n\t\t\t<\/label>\n\n\t\t<\/td>\n\n\t\t<td class=\"radio_cell\"><input type=\"radio\" id=\"desktop_everything_{{channel.id}}\" name=\"desktop_{{channel.id}}\" data-id=\"{{channel.id}}\" data-device=\"desktop\" value=\"everything\" {{#if channel.is_muted}}disabled{{\/if}}><\/td>\n\t\t<td class=\"radio_cell\"><input type=\"radio\" id=\"desktop_mentions_{{channel.id}}\" name=\"desktop_{{channel.id}}\" data-id=\"{{channel.id}}\" data-device=\"desktop\" value=\"mentions\" {{#if channel.is_muted}}disabled{{\/if}}><\/td>\n\t\t<td class=\"radio_cell\"><input type=\"radio\" id=\"desktop_nothing_{{channel.id}}\" name=\"desktop_{{channel.id}}\" data-device=\"desktop\" data-id=\"{{channel.id}}\" value=\"nothing\" {{#if channel.is_muted}}disabled{{\/if}}><\/td>\n\n\t\t<td class=\"radio_cell\"><input type=\"radio\" id=\"mobile_everything_{{channel.id}}\" name=\"mobile_{{channel.id}}\" data-id=\"{{channel.id}}\" data-device=\"mobile\" value=\"everything\" {{#if channel.is_muted}}disabled{{\/if}}><\/td>\n\t\t<td class=\"radio_cell\"><input type=\"radio\" id=\"mobile_mentions_{{channel.id}}\" name=\"mobile_{{channel.id}}\" data-id=\"{{channel.id}}\" data-device=\"mobile\" value=\"mentions\" {{#if channel.is_muted}}disabled{{\/if}}><\/td>\n\t\t<td class=\"radio_cell\"><input type=\"radio\" id=\"mobile_nothing_{{channel.id}}\" name=\"mobile_{{channel.id}}\" data-id=\"{{channel.id}}\" data-device=\"mobile\" value=\"nothing\" {{#if channel.is_muted}}disabled{{\/if}}><\/td>\n\n\t\t<td class=\"align_center\"><i class=\"ts_icon ts_icon_times_circle revert_to_default\" data-id=\"{{channel.id}}\" data-toggle=\"tooltip\" title=\"Clear custom notification settings from {{#if channel.is_channel}}#{{\/if}}{{channel.name}}\"><\/i><\/td>\n\t<\/tr>\n";
TS.raw_templates['password_modal'] = "<form id=\"{{name}}_form\" action=\"{{action}}\" method=\"post\" accept-encoding=\"UTF-8\">\n\t<input type=\"password\" id=\"{{name}}_password\" name=\"{{name}}_password\" placeholder=\"Password\">\n\t<input type=\"hidden\" name=\"crumb\" value=\"{{crumb}}\">\n\t{{#if mode}}<input type=\"hidden\" name=\"{{mode}}\" value=\"1\">{{\/if}}\n<\/form>\n";
TS.raw_templates['account_secret_codes_modal'] = "<p class=\"alert alert_warning\">\n\t<i class=\"ts_icon ts_icon_exclamation_circle code_warning\"><\/i>If you lose access to your authentication device, you can use one of these backup codes to login to your account. Each code may be used only once. Make a copy of these codes, and store it somewhere safe.\n<\/p>\n<div class=\"align_center\">\n\t<div class=\"backup_codes_wrapper margin_auto curved_shadow\">\n\t\t<div class=\"backup_codes\">\n\t\t\t<ul class=\"clearfix\">\n\t\t\t\t{{#each codes}}<li>{{this}}<\/li>{{\/each}}\n\t\t\t<\/ul>\n\t\t<\/div>\n\t<\/div>\n<\/div>";
TS.raw_templates['two_factor_auth_modal'] = "<h1 class=\"medium large_top_margin medium_bottom_margin\">Mandatory two-factor authentication<\/h1>\n<p>All team members (<strong>including you<\/strong>) who do not have two-factor authentication currently enabled will be notified via Slackbot and email to do so. New team members will be asked to configure 2FA as well.<\/p>\n<p class=\"large_bottom_margin\">After 24 hours, members who have not configured 2FA will be required to do so before continuing to use Slack,<\/p>\n<p class=\"bottom_margin\"><strong><a id=\"customize_toggle\" class=\"indifferent_grey\" href=\"#\">Customize Slackbot message <i class=\"ts_icon ts_icon_caret_right\"><\/i><\/a><\/strong><\/p>\n<div id=\"customize\" class=\"customize_wrapper\">\n\t<div class=\"accordion_subsection\" style=\"display: none;\">\n\t\t<div class=\"message_wrapper normal_padding bottom_margin\">\n\t\t\t<div class=\"message show_user avatar first divider\">\n\t\t\t\t<span class=\"member_preview_link member_image thumb_36\" style=\"background-image: url('https:\/\/slack-assets2.s3-us-west-2.amazonaws.com\/10068\/img\/slackbot_48.png')\"><\/span>\n\t\t\t\t<span class=\"message_sender member color_USLACKBOT\">Slackbot<\/span>\n\t\t\t\t<span class=\"message_content bottom_margin\">Hi, {{name}}. Your Team Admin, {{admin_name}}, has enabled mandatory <a href=\"https:\/\/slack.zendesk.com\/hc\/en-us\/articles\/204509068\" target=\"_blank\">two-factor authentication<\/a> for your Slack team. This means that moving forward, you'll need an authentication code from your phone whenever you sign in.<\/span>\n\t\t\t\t<span class=\"message_content bottom_margin\">In {{hours}} ({{time}}) you'll be asked to configure two-factor authentication, but we recommend you do this now.<\/span>\n\t\t\t\t<span class=\"message_content bottom_margin\"><a href=\"\/account\/settings#two_factor\">Configure two-factor authentication now.<\/a><\/span>\n\t\t\t\t<span class=\"message_content bottom_margin custom_message left_padding\">[ Your custom message goes here. ]<\/span>\n\t\t\t<\/div>\n\t\t<\/div>\n\t\t<textarea name=\"custom_message_input\" id=\"custom_message_input\" class=\"custom_message_input\" placeholder=\"Add a message&hellip;\"><\/textarea>\n\t<\/div>\n<\/div>\n<div class=\"clearfix large_top_margin modal_password_form\">\n\t<div class=\"span_4_of_6\">\n\t\t<form action=\"\/admin\/auth\/\" method=\"post\" accept-encoding=\"UTF-8\">\n\t\t\t<label for=\"password\">Your password<\/label>\n\t\t\t<input type=\"hidden\" name=\"crumb\" value=\"{{crumb}}\">\n\t\t\t<input id=\"password\" class=\"bottom_margin\" type=\"password\" placeholder=\"Password\" name=\"password\">\n\t\t\t<button id=\"confirm_btn\" class=\"btn btn_large disabled\">Confirm and enable<\/button>\n\t\t<\/form>\n\t<\/div>\n<\/div>\n<p class=\"subtle_silver align_center\">Learn more about <a class=\"underline subtle_silver\" href=\"\">two-factor authentication<\/a><\/p>\n";
TS.raw_templates['sidebar_theme_css'] = "\t<style type=\"text\/css\" id=\"sidebar_theme_css\">\n\n\t\t\/* Column Background *\/\n\t\t#col_channels_bg,\n\t\t#col_channels {\n\t\t\tbackground: {{theme.column_bg}};\n\t\t}\n\t\t#monkey_scroll_wrapper_for_channels_scroller .monkey_scroll_handle_inner {\n\t\t\tborder-color: {{theme.column_bg}};\n\t\t}\n\n\t\t\/* Menu Background *\/\n\t\t#team_menu {\n\t\t\tbackground: {{theme.menu_bg}};\n\t\t\tcolor: {{theme.text_color}};\n\t\t\ttext-shadow: 0 1px 1px rgba(0, 0, 0, 0.25);\n\t\t}\n\t\t#team_menu,\n\t\t#quick_switcher_btn,\n\t\t#team_menu_overlay,\n\t\t#col_channels_overlay {\n\t\t\tbackground: {{theme.column_bg}};\n\t\t\tborder-color: {{theme.column_bg}};\n\t\t}\n\t\tbody:not(.loading) #team_menu:hover,\n\t\tbody:not(.loading) #team_menu.active,\n\t\t#quick_switcher_btn:hover,\n\t\t#quick_switcher_btn:active {\n\t\t\tbackground: {{theme.menu_bg}};\n\t\t\tborder-color: {{theme.menu_bg}};\n\t\t}\n\n\t\t\/* Active Item *\/\n\t\t#channels_scroller.show_which_channel_is_active ul li.active a.channel_name, \n\t\t#channels_scroller.show_which_channel_is_active ul li.active a.group_name, \n\t\t#channels_scroller.show_which_channel_is_active ul li.active a.im_name, \n\t\t#channels_scroller.show_which_channel_is_active ul li.active a.mpim_name,\n\t\t#channels_nav ul li.unread_link.active a {\n\t\t\tbackground: {{theme.active_item}};\n\t\t\topacity: 1;\n\t\t\tcolor: {{theme.active_item_text}} !important;\n\t\t}\n\t\t#channels_scroller.show_which_channel_is_active ul li.active .unread_highlights,\n\t\t#channels_nav li.active {\n\t\t\tcolor: {{theme.active_item}};\n\t\t}\n\t\t#channel_scroll_up {\n\t\t\tbox-shadow: 0 1px 1px rgba(0,0,0,0.25), inset 0px 2px 2px rgba(0,0,0,0.1)\n\t\t}\n\t\t#channel_scroll_down {\n\t\t\tbox-shadow: 0 -1px 1px rgba(0,0,0,0.25), inset 0px -2px 2px rgba(0,0,0,0.1)\n\t\t}\n\n\t\t\/* Hovered Item *\/\n\t\tbody:not(.sorting_mode) .channels_list_holder ul li a:hover,\n\t\t#monkey_scroll_wrapper_for_channels_scroller .monkey_scroll_bar {\n\t\t\tbackground: {{theme.hover_item}};\n\t\t}\n\t\t\/* Separated b\/c FF will drop all rules since it doesn't recognize -webkit-scrollbar *\/\n\t\t.supports_custom_scrollbar #channels_scroller::-webkit-scrollbar-track {\n\t\t\tbackground: transparent;\n\t\t\tcolor: transparent;\n\t\t}\n\t\t.supports_custom_scrollbar #col_channels:hover #channels_scroller::-webkit-scrollbar-track {\n\t\t\tbackground: {{theme.hover_item}};\n\t\t\tcolor: {{theme.column_bg}};\n\t\t}\n\n\t\t\/* Text Color *\/\n\t\t.channels_list_holder ul li a,\n\t\t.channels_list_holder h2,\n\t\t.list_more,\n\t\t#team_menu:before,\n\t\t#team_menu .notifications_menu_btn,\n\t\t#team_menu .team_name_caret,\n\t\t#channels_scroller.show_which_channel_is_active ul li.active a.channel_name .prefix, \n\t\t#channels_scroller.show_which_channel_is_active ul li.active a.group_name .prefix,\n\t\t#team_header_user_name,\n\t\t#channels_nav ul li button,\n\t\t.channels_list_holder ul li.unread .prefix,\n\t\t.channels_list_holder ul li.member .im_close, \n\t\t.channels_list_holder ul li.mpim .mpim_close,\n\t\t.channels_list_holder ul li.group .group_close,\n\t\t#channels_nav ul li a,\n\t\t#quick_switcher_btn > i {\n\t\t\tcolor: {{theme.text_color}} !important;\n\t\t\topacity: 0.6;\n\t\t}\n\t\t.channels_list_new_btn {\n\t\t\tcolor: {{theme.text_color}};\n\t\t}\n\t\t#quick_switcher_label,\n\t\t#quick_switcher_shortcut,\n\t\t#quick_switcher_btn:hover #quick_switcher_shortcut {\n\t\t\tcolor: {{theme.text_color}} !important;\n\t\t}\n\t\t.channel_list_add_link {\n\t\t\tcolor: {{theme.text_color}} !important;\n\t\t}\n\t\t.channel_list_add_link:hover {\n\t\t\tborder-color: {{theme.text_color}};\n\t\t}\n\t\t.overlay_mock_text{\n\t\t\tbackground: {{theme.text_color}} !important;\n\t\t}\n\t\t#team_menu:hover:before,\n\t\t#team_menu.active:before,\n\t\t.channels_list_holder h2.hoverable:hover,\n\t\t#team_menu:hover .team_name_caret,\n\t\t#team_menu.active .team_name_caret {\n\t\t\topacity: 1;\n\t\t}\n\t\t#channels_scroller.show_which_channel_is_active ul li.active a.channel_name .prefix, \n\t\t#channels_scroller.show_which_channel_is_active ul li.active a.group_name .prefix {\n\t\t\tcolor: {{theme.active_item_text}} !important;\t\n\t\t}\n\t\t.list_more:hover {\n\t\t\tcolor: {{theme.text_color}} !important;\n\t\t\tborder-bottom-color: {{theme.text_color}};\n\t\t}\n\n\t\t.channels_list_holder ul li.unread a,\n\t\t.channels_list_holder ul li .channels_list_new_btn:hover,\n\t\t.channels_list_holder ul li.member .im_close, \n\t\t.channels_list_holder ul li.mpim .mpim_close,\n\t\t.channels_list_holder ul li.group .group_close {\n\t\t\topacity: 1 !important;\n\t\t}\n\n\t\t#monkey_scroll_wrapper_for_channels_scroller .monkey_scroll_handle_inner {\n\t\t\tbackground: {{theme.text_color}};\n\t\t\topacity: 0.7;\n\t\t}\n\t\t\/* Separated b\/c FF will drop all rules since it doesn't recognize -webkit-scrollbar *\/\n\t\t.supports_custom_scrollbar #channels_scroller::-webkit-scrollbar-thumb {\n\t\t\tbackground: transparent;\n\t\t\tcolor: transparent;\n\t\t}\n\t\t.supports_custom_scrollbar #col_channels:hover #channels_scroller::-webkit-scrollbar-thumb {\n\t\t\tbackground: rgba({{theme.text_color_rgb.r}}, {{theme.text_color_rgb.g}}, {{theme.text_color_rgb.b}}, .7);\n\t\t\tcolor: {{theme.column_bg}};\n\t\t}\n\n\t\t\/* Presence *\/\n\t\t#channels_scroller .presence.active i.presence_icon,\n\t\t#channel_header #presence.active:before,\n\t\t#client_header #presence.active:before,\n\t\t.slackbot_icon {\n\t\t\tcolor: {{theme.active_presence}};\n\t\t\topacity: 1;\n\t\t}\n\n\t\t#channels_scroller.show_which_channel_is_active li.active .presence.active i.presence_icon,\n\t\t#channels_scroller.show_which_channel_is_active li.active .slackbot_icon {\n\t\t\tcolor: {{theme.active_item_text}} !important;\t\n\t\t}\n\n\t\t\/* Override for Hoth Theme presence dot on selected item. Fixes Bug #7154 *\/\n\t\t.sidebar_theme_hoth_theme #channels_scroller.show_which_channel_is_active li.active .presence.active i.presence_icon,\n\t\t.sidebar_theme_hoth_theme #channels_scroller.show_which_channel_is_active li.active .slackbot_icon {\n\t\t\tcolor: {{theme.active_presence}} !important;\t\n\t\t}\n\n\t\t#channels_scroller.show_which_channel_is_active li.active .presence.away i.presence_icon {\n\t\t\tcolor: {{theme.active_item_text}} !important;\n\t\t\topacity: 0.3;\n\t\t}\n\t\t#channels_scroller .presence.away,\n\t\t#channels_scroller .presence.away,\n\t\t#channel_header #presence.away:before {\n\t\t\tcolor: {{theme.text_color}};\n\t\t\topacity: 0.3;\n\t\t}\n\n\t\t\/* Badge + Mentions bar *\/\n\t\t.channels_list_holder .unread_highlights,\n\t\t#channel_scroll_up.unseen_have_mentions, \n\t\t#channel_scroll_down.unseen_have_mentions,\n\t\tts-jumper ol li .unread_count {\n\t\t\tbackground: {{theme.badge}};\n\t\t}\n\n\t\t\/* No text shadows *\/\n\t\t#col_channels a,\n\t\t#team_menu {\n\t\t\ttext-shadow: none !important;\n\t\t}\n\n\t<\/style>\n";
TS.raw_templates['billing_contact'] = "\t<div class=\"billing_contact {{#if member.is_primary_owner}}primary_owner{{\/if}} {{#if member.deleted}}inactive{{\/if}}\" data-contact-id=\"{{contact.id}}\">\n\t\t{{#if member}}\n\t\t\t{{{makeMemberPreviewLinkImage member.id 48}}}\n\t\t\t<div class=\"inline_block\" style=\"margin-left: 8px\">\n\t\t\t\t<div class=\"no_wrap\">\n\t\t\t\t\t<strong>{{#if member.is_self}}You &bull; {{\/if}}{{#feature flag=\"feature_name_tagging_client\"}}{{getMemberFullName member}}{{else}}{{getMemberDisplayName member}}{{\/feature}}<\/strong>\n\t\t\t\t\t{{#if member.deleted}}&bull; <em class=\"subtle_silver\">(Disabled)<\/em>{{\/if}}\n\t\t\t\t<\/div>\n\t\t\t\t<div>{{member_type}} {{#unless member.is_primary_owner}}&bull; {{member.profile.email}}{{\/unless}}<\/div>\n\t\t\t<\/div>\n\t\t\t{{#if is_primary_and_self}}\n\t\t\t\t<label class=\"checkbox primary_owner_toggle_label\" for=\"primary_owner_toggle\">\n\t\t\t\t\t<input id=\"primary_owner_toggle\" type=\"checkbox\"> Send billing emails to you at <strong>{{member.profile.email}}<\/strong>\n\t\t\t\t<\/label>\n\t\t\t{{\/if}}\n\t\t{{else}}\n\t\t\t<div class=\"inline_block\" style=\"margin-left: 56px\">\n\t\t\t\t<div class=\"no_wrap\"><strong>{{contact.email}}<\/strong><\/div>\n\t\t\t\t<div>Billing Contact<\/div>\n\t\t\t<\/div>\n\t\t{{\/if}}\n\t\t{{#unless member.is_primary_owner}}\n\t\t\t<a class=\"remove_contact\"><i class=\"ts_icon ts_icon_times float_right\"><\/i><\/a>\n\t\t{{\/unless}}\n\t<\/div>\n";
TS.raw_templates['billing_add_contact_form'] = "\t<div id=\"add_contact_form\" class=\"top_margin\">\n\t\t<div>\n\t\t\t<div>Add a billing contact from your team:<\/div>\n\t\t\t<div class=\"add_contact_input\">\n\t\t\t\t<select id=\"user_contact_select\" multiple data-placeholder=\"Type the new billing contact's name\">\n\t\t\t\t\t{{#each members}}\n\t\t\t\t\t\t<option value=\"{{id}}\">{{#feature flag=\"feature_name_tagging_client\"}}{{getMemberFullName this}} ({{profile.email}}){{else}}{{#if real_name}}{{real_name}} &bull;{{\/if}} {{name}}{{\/feature}}<\/option>\n\t\t\t\t\t{{\/each}}\n\t\t\t\t<\/select>\n\t\t\t<\/div>\n\t\t<\/div>\n\n\t\t<div class=\"small_top_margin\">\n\t\t\t<div>Or by email address:<\/div>\n\t\t\t<input id=\"email_contact_input\" class=\"add_contact_input small no_bottom_margin\" type=\"text\" placeholder=\"name@domain.com\">\n\t\t<\/div>\n\n\t\t<button class=\"btn ladda-button top_margin\" data-style=\"expand-right\"><span class=\"ladda-label\">Add billing contact<\/span><\/button>\n\t<\/div>\n";
TS.raw_templates['billing_history_list'] = "<table class=\"full_width\">\n\t<tr>\n\t\t<th class=\"billing_list_cell cell_date hide_on_mobile\">Date<\/th>\n\t\t<th>Item<\/th>\n\t\t<th class=\"billing_list_cell cell_charges\">Charges<\/th>\n\t\t<th class=\"billing_list_cell cell_credit_balance\">Credit Balance<\/th>\n\t<\/tr>\n\t{{#each history}}\n\t<tr>\n\t\t<td class=\" billing_list_cell cell_date hide_on_mobile\">\n\t\t\t{{#if this.has_billing_statement}}\n\t\t\t\t<a href=\"\/admin\/billing\/{{this.id}}\">{{this.formatted_date}}<\/a>\n\t\t\t{{else}}\n\t\t\t\t{{this.formatted_date}}\n\t\t\t{{\/if}}\n\t\t<\/td>\n\t\t<td class=\"billing_list_cell item\">\n\t\t\t{{> billing_history_list_item_col this}}\n\t\t<\/td>\n\t\t<td class=\"billing_list_cell cell_charges\">\n\t\t\t<span>{{this.charges}}<\/span><\/div>\n\t\t<\/td>\n\t\t<td class=\"billing_list_cell cell_credit_balance\">\n\t\t\t<span>${{this.credit_balance}}<\/span><\/div>\n\t\t<\/td>\n\t<\/tr>\n\t{{\/each}}\n<\/table>";
TS.raw_templates['billing_history_list_item_col'] = "{{#if is_event}}\n\t{{event_desc}}\n{{else if is_billing}}\n\t{{#unless is_monthly_statement}}\n\t<div class=\"show_on_mobile\">\n\t\t{{#if has_billing_statement}}\n\t\t\t<a href=\"\/admin\/billing\/{{id}}\">\n\t\t\t\t{{formatted_date}}\n\t\t\t<\/a>\n\t\t{{else}}\n\t\t\t{{formatted_date}}\n\t\t{{\/if}}\n\t<\/div>\n\t{{\/unless}}\n\t{{#if has_billing_statement}}\n\t\t<a href=\"\/admin\/billing\/{{id}}\" class=\"link_billing_statement\">\n\t\t\t<span>{{> billing_history_item_strings this}}<\/span>\n\t\t<\/a>\n\t{{else}}\n\t\t<span>{{> billing_history_item_strings this}}<\/span>\n\t{{\/if}}\n\t{{#if has_billing_statement}}\n\t\t<span>(<a href=\"\/admin\/billing\/{{id}}\/pdf\">PDF<\/a>)<\/span>\n\t{{\/if}}\n{{else}}\n\t<span>{{> billing_history_item_strings this}}<\/span>\n{{\/if}}\n<br \/>\n{{#if_equal type compare=\"invoice\"}}\n\t<span class=\"link_invoice_id\">Invoice #{{id}}<\/span>\n{{\/if_equal}}\n{{#if has_billing_statement}}\n\t<span><a href=\"\/admin\/billing\/{{id}}\" class=\"link_statement_id\">Billing Statement #{{id}}<\/a><\/span>\n{{\/if}}";
TS.raw_templates['billing_history_item_strings'] = "{{!--\nBilling item type to string partial\nUsed in billing_history_list_item_col.hbs\n--}}\n\n{{!-- types that have a billing statement associated --}}\n{{#if_equal type compare=\"monthly_statement\"}}\n\t<a href=\"\/admin\/billing\/{{this.id}}\" class=\"link_billing_statement\">Monthly Statement\n\t<span class=\"monthly_statement_date\"> \u2013 {{this.formatted_date}}<\/span><\/a>\n{{\/if_equal}}\n{{#if_equal type compare=\"invoice_switch\"}}\n\tSwitched to invoice\n{{\/if_equal}}\n{{#if_equal type compare=\"invoice\"}}\n\tInvoice\n{{\/if_equal}}\n{{#if_equal type compare=\"invoice_adjust\"}}\n\tInvoice adjusted\n{{\/if_equal}}\n{{#if_equal type compare=\"purchase\"}}\n\tPurchased the <strong>{{product.type}} plan<\/strong>{{#if product.discount}}<span class=\"inline_discount\"> (Discounted{{#if product.discount.is_simple_discount}} {{product.discount.percentage}}%{{\/if}})<\/span>{{\/if}}, paying {{#if_equal product.term compare=\"yearly\"}}Annually{{else}}Monthly{{\/if_equal}}\n{{\/if_equal}}\n{{#if_equal type compare=\"renewal\"}}\n\tRenewed the <strong>{{product.type}} plan<\/strong>{{#if product.discount}}<span class=\"inline_discount\"> (Discounted{{#if product.discount.is_simple_discount}} {{product.discount.percentage}}%{{\/if}})<\/span>{{\/if}}, paying {{#if_equal product.term compare=\"yearly\"}}Annually{{else}}Monthly{{\/if_equal}}\n{{\/if_equal}}\n{{#if_equal type compare=\"cancel\"}}\n\tSwitched to the <strong>Free plan<\/strong>\n{{\/if_equal}}\n{{#if_equal type compare=\"switch\"}}\n\tSwitched to the <strong>{{product.type}} plan<\/strong>, paying {{#if_equal product.term compare=\"yearly\"}}Annually{{else}}Monthly{{\/if_equal}}\n{{\/if_equal}}\n{{#if_equal type compare=\"future_cancel\"}}\n\tSwitch to the <strong>Free plan<\/strong> at renewal time\n{{\/if_equal}}\n{{#if_equal type compare=\"future_switch\"}}\n\tSwitch to the <strong>{{product.type}} plan<\/strong> at renewal time, paying {{#if_equal product.term compare=\"yearly\"}}Annually{{else}}Monthly{{\/if_equal}}\n{{\/if_equal}}\n{{#if_equal type compare=\"users_change\"}}\n\tSwitched from <strong>{{num_users_from}} to {{num_users_to}}<\/strong> {{pluralize num_users_to 'user' 'users'}}\n{{\/if_equal}}\n{{#if_equal type compare=\"late\"}}\n\tPaid money owed\n{{\/if_equal}}\n{{#if_equal type compare=\"expire\"}}\n\tPaid plan expired\n{{\/if_equal}}\n{{#if_equal type compare=\"comp_gift\"}}\n\tGiven the <strong>{{product.typ}} plan<\/strong>\n{{\/if_equal}}\n{{#if_equal type compare=\"trial_start\"}}\n\tStarted a free trial\n{{\/if_equal}}\n{{#if_equal type compare=\"charge_high_balance\"}}\n\tCharged for high balance\n{{\/if_equal}}\n{{#if_equal type compare=\"annual_team_balance_paid\"}}\n\tMonthly accrued balance for annual teams\n{{\/if_equal}}\n{{!-- Types with no billing statment associated, will not link --}}\n{{#if_equal type compare=\"staff_gift\"}}\n\t{{#if_gt charges.delta compare=0}}\n\t\tGift from Slack\n\t{{else}}\n\t\tCredit adjustment\n\t{{\/if_gt}}\n{{\/if_equal}}\n{{#if_equal type compare=\"referral_purchase_credit\"}}\n\tCredit for referring a new team\n{{\/if_equal}}\n{{#if_equal type compare=\"referral_credit\"}}\n\tReferral signup credit\n{{\/if_equal}}\n{{#if_equal type compare=\"beta_credit\"}}\n\tBeta testing credit\n{{\/if_equal}}\n{{#if_equal type compare=\"launch_credit\"}}\n\tNew team credit\n{{\/if_equal}}\n{{#if_equal type compare=\"credit_test_group\"}}\n\tNew team credit\n{{\/if_equal}}\n{{#if_equal type compare=\"survey_credit\"}}\n\tSurvey completed\n{{\/if_equal}}\n{{#if_equal type compare=\"txn_void\"}}\n\tVoided Payment\n{{\/if_equal}}\n{{#if_equal type compare=\"txn_refund\"}}\n\tRefunded Payment\n{{\/if_equal}}\n{{#if_equal type compare=\"enterprise_transfer\"}}\n\t{{#if enterprise_transfer.enterprise_id}}\n\t\tTransferred to <a href=\"{{enterprise_transfer.enterprise_url}}\">{{enterprise_transfer.enterprise_name}}<\/a>\n\t{{else if enterprise_transfer.team_id}}\n\t\tTransferred to <a href=\"{{enterprise_transfer.team_url}}\">{{enterprise_transfer.team_name}}<\/a>\n\t{{else}}\n\t\tEnterprise plan transfer\n\t{{\/if}}\n{{\/if_equal}}\n{{#if_equal type compare=\"transfer\"}}\n\t\tTransferred ${{charges.delta}}{$row.delta|abs|cash} {{#if_gt charges.delta compare=0}}from{{else}}to{{\/if_gt}} {{transfer.other_team_type}}\n\t{{#if_equal transfer.reason compare='payments_group'}}\n\t\t(payment group transfer)\n\t{{\/if_equal}}\n\t{{#if_equal transfer.reason compare='enterprise'}}\n\t\t(enterprise plan transfer)\n\t{{\/if_equal}}\n{{\/if_equal}}\n\n";
TS.raw_templates['bank_account_verification_dialog'] = "\t<p>Enter the number of cents (between 1 and 100) of the two deposits here to verify your account. <\/p>\n\t<input type=\"text\" name=\"value1\" id=\"value1\" placeholder=\"deposit 1\">\n\t<input type=\"text\" name=\"value2\" id=\"value2\" placeholder=\"deposit 2\">\n\t<p class=\"mini\">Within 1-2 business days of adding your bank account, you will receive two small deposits in your account with statement description <code>VERIFICATION<\/code>.<\/p>";
