TS.raw_templates['shared_channels_invites_modal'] = "<div id=\"sci_container\" class=\"channel_modal_with_list channel_modal\">\n\n\t<div id=\"sci_header_wrapper\">\n\t\t<div class=\"sci_header_container large_bottom_margin display_flex align_items_center\">\n\t\t\t<h1 class=\"sci_header\">Shared Channels<\/h1>\n\n\t\t\t{{#if show_create_shared_channel_btn}}<button class=\"btn auto_left_margin\" data-action=\"sci_to_create\">New Shared Channel<\/button>{{\/if}}\n\t\t<\/div>\n\n\t\t<div class=\"small_bottom_margin display_flex align_items_center\">\n\t\t\t<div class=\"channel_browser_filter_container channel_modal_filter_container right_margin flex_one\">\n\t\t\t\t<i class=\"ts_icon ts_icon_search cloud_silver search_icon\"><\/i>\n\t\t\t\t<i class=\"ts_icon ts_icon_times_circle cloud_silver clear_filter_icon\"><\/i>\n\t\t\t\t<input type=\"text\" id=\"channel_browser_filter\" class=\"small\" placeholder=\"Search channels\">\n\t\t\t<\/div>\n\t\t\t<div class=\"channel_browser_sort_container\">\n\t\t\t\t<label class=\"select small\">\n\t\t\t\t\t<select id=\"channel_browser_sort\" class=\"small\">\n\t\t\t\t\t\t<option value=\"name\">Channel Name &amp; Membership<\/option>\n\t\t\t\t\t\t<option value=\"creator\">Name of Creator<\/option>\n\t\t\t\t\t\t<option value=\"created\">Creation Date (newest first)<\/option>\n\t\t\t\t\t\t<option value=\"members_high\">Members (most to fewest)<\/option>\n\t\t\t\t\t\t<option value=\"members_low\">Members (fewest to most)<\/option>\n\t\t\t\t\t<\/select>\n\t\t\t\t<\/label>\n\t\t\t<\/div>\n\t\t<\/div>\n\t<\/div>\n\n\t<div id=\"sci_loading\">{{loadingHTML}}<\/div>\n\n\t<div id=\"sci_no_shared_channels\" class=\"display_flex justify_content_between align_items_center hidden\">\n\t\t<span class=\"sci_no_shared_note\">You don't have any shared channels yet.<\/span>\n\t<\/div>\n\n\t<div id=\"sci_channels_container\" class=\"display_flex hidden\">\n\t\t<div class=\"list_container\">\n\t\t\t<div class=\"list\"><\/div>\n\t\t<\/div>\n\t<\/div>\n\n\t<div id=\"sci_channels_create\"><\/div>\n\n\t<div id=\"sci_send\"><\/div>\n\n\t<div id=\"sci_invites_container\"><\/div>\n<\/div>\n";
TS.raw_templates['shared_channels_invites_create'] = "<h1 class=\"sci_header\">Create a Shared Channel<\/h1>\n\n<div class=\"channels_create_container\">\n\t<span class=\"sci_sub_header\">{{#if enterprise_info}}\n\t\tMembers of separate teams at {{enterprise_info.name}} can send messages and share files with each other in a shared channel.\n\t{{else}}\n\t\tMembers of the other team will only be able to see files and members inside this shared channel.\n\t{{\/if}}<\/span>\n\n\t<label>Access for your team members<\/label>\n\t<label for=\"access\"><input type=\"checkbox\" name=\"access\" checked><\/label>\n\n\t{{#if enterprise_info}}\n\t\t<label>Share this channel with<\/label>\n\t\t<label class=\"sci_radio\"><input type=\"radio\" name=\"share_with\" value=\"all\" checked>All {{enterprise_info.name}} teams<\/label>\n\t\t{{#feature flag=\"feature_external_shared_channels_ui\"}}\n\t\t\t<label class=\"sci_radio\"><input type=\"radio\" name=\"share_with\" value=\"specific\">Specific {{enterprise_info.name}} teams<\/label>\n\t\t\t<label class=\"sci_radio inline_block\"><input type=\"radio\" name=\"share_with\" value=\"external\">External teams<\/label><label for=\"share_with_specific\"><\/label><label for=\"share_with_external\"><\/label>\n\t\t{{else}}\n\t\t\t<label class=\"sci_radio inline_block\"><input type=\"radio\" name=\"share_with\" value=\"specific\">Specific {{enterprise_info.name}} teams<\/label><label for=\"share_with_specific\"><\/label>\n\t\t{{\/feature}}\n\n\t\t<div id=\"sci_specific_team\" class=\"hidden\">\n\t\t\t<input type=\"text\" name=\"domains\" placeholder=\"Add teams\" data-validation=\"required\" data-validation-for=\"share_with_specific\">\n\t\t<\/div>\n\t{{\/if}}\n\n\t{{#feature flag=\"feature_external_shared_channels_ui\"}}\n\t\t<div id=\"sci_external_team\"{{#if enterprise_info}} class=\"hidden\"{{\/if}}>\n\t\t\t<p class=\"position_relative no_margin\">\n\t\t\t\t{{#unless enterprise_info}}<label for=\"domain\">URL for the Slack team you are inviting<\/label>{{\/unless}}\n\t\t\t\t<span class=\"placeholder_overlay\"><\/span>\n\t\t\t\t<input type=\"text\" name=\"domain\" placeholder=\"teamurl\" data-validation=\"required reservedwords={{reserved_domain}} valid_invite\"{{#if enterprise_info}} data-validation-for=\"share_with_external\"{{\/if}} autocapitalize=\"off\" autocorrect=\"off\" autocomplete=\"off\">\n\t\t\t<\/p>\n\t\t\t<span class=\"input_note\">Ask a member of the other team for their Team URL (<a href=\"https:\/\/get.slack.help\/hc\/en-us\/articles\/212681477-Signing-in-to-Slack\" target=\"{{newWindowName}}\">How they can find it<\/a>).<\/span>\n\t\t<\/div>\n\t{{\/feature}}\n\n\t<label for=\"channel_name\">Name this channel (for your team)<\/label>\n\t<input type=\"text\" name=\"channel_name\" placeholder=\"Enter name (e.g. marketing-agency)\" data-validation=\"channel_name valid_invite\" autocapitalize=\"off\" autocorrect=\"off\" autocomplete=\"off\">\n\t<span class=\"input_note\">Names must be lowercase, no more than 21 characters, and cannot contain spaces or periods.<\/span>\n\n\t<label for=\"purpose\">Purpose <span class=\"normal old_petunia_grey\">(Optional)<\/span><\/label>\n\t<textarea name=\"purpose\" placeholder=\"Briefly describe the purpose of this channel\" data-validation=\"maxlength=250\"><\/textarea>\n\n\t<button class=\"btn btn_large ladda-button\" data-style=\"expand-right\" data-action=\"sci_channels_create\">Create Shared Channel<\/button>\n\t<button class=\"btn btn_large btn_outline\" data-action=\"sci_cancel\">Cancel<\/button>\n<\/div>\n";
TS.raw_templates['shared_channels_invites_send'] = "<h1 class=\"sci_header\">Send Invitation<\/h1>\n<span class=\"sci_sub_header\">Invite the other team by sharing the link below or sending an email. You\u2019ll be notified when your shared channel invitation is accepted and ready to be activated.<\/span>\n\n<div class=\"sci_copy_link_container\" data-action=\"sci_show_action\">\n\t<div class=\"display_flex align_items_center charcoal_grey\">\n\t\t<span class=\"sci_image_bubble\"><\/span>\n\t\t<span>Share Link<\/span>\n\t\t<ts-icon class=\"ts_icon_chevron_medium_right auto_left_margin\"><\/ts-icon>\n\t<\/div>\n\n\t<div class=\"display_flex align_items_center flex_wrap\">\n\t\t<label for=\"copy_link_{{invite_id}}\" class=\"full_width\">Copy link<span class=\"validation_message overflow_ellipsis old_petunia_grey inline hidden\" title=\"Right-click to copy and share this link.\"><\/span><\/label>\n\t\t<input type=\"text\" name=\"copy_link_{{invite_id}}\" class=\"input_inline small no_margin flex_one\" value=\"{{invite_url}}\" readonly data-invite-id=\"{{invite_id}}\">\n\t\t<button class=\"btn btn_outline ladda-button\" data-style=\"expand-right\" data-action=\"sci_copy\" data-invite-id=\"{{invite_id}}\">Copy Link<\/button>\n\t<\/div>\n<\/div>\n\n<div class=\"sci_send_email_container\" data-action=\"sci_show_action\">\n\t<div class=\"display_flex align_items_center charcoal_grey\">\n\t\t<span class=\"sci_image_bubble\"><\/span>\n\t\t<span>Send Email<\/span>\n\t\t<ts-icon class=\"ts_icon_chevron_medium_right auto_left_margin\"><\/ts-icon>\n\t<\/div>\n\n\t<div class=\"display_flex align_items_center flex_wrap\">\n\t\t<label for=\"send_email_{{invite_id}}\" class=\"full_width\">Send email<\/label>\n\t\t<input type=\"text\" name=\"send_email_{{invite_id}}\" class=\"input_inline small no_margin flex_one\" placeholder=\"Enter invitee\u2019s email address\" data-validation=\"is_email\" data-invite-id=\"{{invite_id}}\" autocapitalize=\"off\" autocorrect=\"off\" autocomplete=\"off\">\n\t\t<button class=\"btn btn_outline\" data-action=\"sci_send\" data-invite-id=\"{{invite_id}}\">Send Email<\/button>\n\t\t<div data-action=\"replace_sent_emails\" class=\"hidden\"><\/div>\n\t<\/div>\n<\/div>\n\n<button class=\"btn btn_large\" data-action=\"sci_cancel\">Done<\/button>\n";
TS.raw_templates['shared_channels_invites_invite'] = "<div id=\"sci_invite_container_{{invite_id}}\" class=\"sci_invite_container\" data-action=\"sci_toggle_contents\">\n\t<div class=\"sci_list_text display_flex align_items_center\">\n\t\t<img class=\"sci_rounded align_self_start\" src=\"{{team.icon.image_88}}\" srcset=\"{{team.icon.image_44}} 1x, {{team.icon.image_88}} 2x\"\/>\n\t\t<div>\n\t\t\t<span class=\"sci_list_team_name block bold\">{{team.name}}<\/span>\n\t\t\t<span class=\"block\">Invited to <strong>{{channel_name}}<\/strong> by <strong>{{getMemberDisplayNameById user}}<\/strong> &middot; {{toTimeAgo date_create}}<\/span>\n\t\t<\/div>\n\t\t<span class=\"sci_pending auto_left_margin\">Pending<ts-icon class=\"sci_toggle_icon ts_icon_caret_right charcoal_grey left_margin\"><\/ts-icon><\/span>\n\t<\/div>\n\n\t<div class=\"sci_list_text\">\n\t\t{{#if purpose}}\n\t\t\t<span class=\"block bold top_margin\">Purpose:<\/span>\n\t\t\t<span class=\"block\">{{purpose}}<\/span>\n\t\t{{\/if}}\n\n\t\t{{> shared_channels_invites_emailed_invitations }}\n\n\t\t<span class=\"top_margin block\">\n\t\t\t<a class=\"sci_list_link\" data-action=\"sci_toggle_send_email\">Send another invite email<\/a>\n\t\t\t<a class=\"sci_list_link\" data-action=\"sci_toggle_copy_link\">Invite by link<\/a>\n\t\t\t<a class=\"sci_list_link sci_danger_link\" data-action=\"sci_revoke\" data-invite-id=\"{{invite_id}}\">Cancel shared channel<\/a>\n\t\t<\/span>\n\n\t\t<div class=\"sci_send_email_mini_container display_flex align_items_center flex_wrap hidden\">\n\t\t\t<input type=\"text\" name=\"invite_send_email_{{invite_id}}\" class=\"input_inline small flex_one\" placeholder=\"Enter invitee\u2019s email address\" data-validation=\"is_email\" data-invite-id=\"{{invite_id}}\" autocapitalize=\"off\" autocorrect=\"off\" autocomplete=\"off\">\n\t\t\t<button class=\"btn btn_outline\" data-action=\"sci_send\" data-invite-id=\"{{invite_id}}\">Send Email<\/button>\n\t\t\t<label for=\"invite_send_email_{{invite_id}}\"><\/label>\n\t\t<\/div>\n\n\t\t<div class=\"sci_copy_link_mini_container display_flex align_items_center flex_wrap hidden\">\n\t\t\t<input type=\"text\" name=\"invite_copy_link_{{invite_id}}\" class=\"input_inline small flex_one\" value=\"{{invite_url}}\" readonly data-invite-id=\"{{invite_id}}\">\n\t\t\t<button class=\"btn btn_outline\" data-action=\"sci_copy\" data-invite-id=\"{{invite_id}}\">Copy Link<\/button>\n\t\t\t<label for=\"invite_copy_link_{{invite_id}}\"><span class=\"validation_message overflow_ellipsis old_petunia_grey inline hidden\" title=\"Right-click to copy and share this link.\"><\/span><\/label>\n\t\t<\/div>\n\t<\/div>\n<\/div>";
TS.raw_templates['shared_channels_invites_emailed_invitations'] = "<div data-action=\"replace_emailed_invites\"{{#unless sent}} class=\"hidden\"{{\/unless}}>\n\t{{#if sent}}\n\t\t<span class=\"block bold top_margin\">Emailed Invites:<\/span>\n\t\t{{#each sent}}\n\t\t\t<span class=\"block\">{{email}} &middot; {{toTimeAgo date_sent}}<\/span>\n\t\t{{\/each}}\n\t{{\/if}}\n<\/div>";
TS.raw_templates['shared_channels_invites_sent_emails'] = "<div data-action=\"replace_sent_emails\" {{#unless sent}}class=\"hidden\"{{\/unless}}>\n\t{{#if sent}}\n\t\t<span class=\"dusty_springfield_grey\"><ts-icon class=\"ts_icon_check_circle_o seafoam_green\"><\/ts-icon> {{pluralize sent.length 'Email' 'Emails'}} sent to\n\t\t\t{{#each sent}}\n\t\t\t\t<strong>{{email}}<\/strong>{{#unless @last}}, {{\/unless}}\n\t\t\t{{\/each}}\n\t\t<\/span>\n\t{{\/if}}\n<\/div>";
TS.raw_templates['shared_channels_invites_channel'] = "<div class=\"sci_channel_container\" data-action=\"sci_toggle_contents\" data-channel-id=\"{{channel}}\">\n\t<div class=\"sci_list_text display_flex align_items_center\">\n\t\t<img class=\"sci_rounded align_self_start\" src=\"{{team.icon.image_88}}\" srcset=\"{{team.icon.image_44}} 1x, {{team.icon.image_88}} 2x\"\/>\n\t\t<div>\n\t\t\t<span class=\"sci_list_team_name block bold\">{{team.name}}<\/span>\n\t\t\t<span class=\"block\">Shares the channel <strong>{{name}}<\/strong><\/span>\n\t\t<\/div>\n\t\t<ts-icon class=\"sci_toggle_icon ts_icon_caret_right auto_left_margin\"><\/ts-icon>\n\t<\/div>\n\n\t<div class=\"sci_list_text\">\n\t\t<span class=\"block bold top_margin\">Shared by:<\/span>\n\t\t<span class=\"block\"><strong>{{getMemberDisplayNameById user}}<\/strong> at {{currentTeamName}}{{#if accepted_user}} and <strong>{{getMemberDisplayNameById accepted_user}}<\/strong> at {{team.name}}{{\/if}} on {{toCalendarDateShort date_create}}<\/span>\n\n\t\t{{#and purpose purpose.value}}\n\t\t\t<span class=\"block bold top_margin\">Purpose:<\/span>\n\t\t\t<span class=\"block\">{{purpose.value}}<\/span>\n\t\t{{\/and}}\n\n\t\t<span class=\"top_margin block\">\n\t\t\t<a class=\"sci_list_link\" data-action=\"sci_archive\" data-channel-id=\"{{channel}}\">Archive shared channel<\/a>\n\t\t<\/span>\n\t<\/div>\n<\/div>";
TS.raw_templates['shared_channels_channel_meta_info'] = "across\n{{#if all_teams}}\n\tall teams in your organization\n{{else}}\n\t<a class=\"ts_tip ts_tip_top ts_tip_float ts_tip_multiline\">\n\t\t{{model_ob.shares.length}} {{pluralize model_ob.shares.length 'team' 'teams'}}\n\t\t<span class=\"ts_tip_tip\">\n\t\t\t<span class=\"ts_tip_multiline_inner\">\n\t\t\t\t{{#each model_ob.shares}}{{#unless @first}}{{#if_gt ..\/model_ob.shares.length compare=2}}, {{\/if_gt}}{{\/unless}}{{#if @last}}{{#if_gt ..\/model_ob.shares.length compare=1}}and {{\/if_gt}}{{\/if}}{{team.name}}{{\/each}}\n\t\t\t<\/span>\n\t\t<\/span>\n\t<\/a>\n{{\/if}}\n";
TS.raw_templates['invite_team_small'] = "<div class=\"invite_team_small clearfix display_flex align_items_center\">\n\t<img class=\"invite_team_small_icon\" src=\"{{team.icon.image_44}}\" srcset=\"{{team.icon.image_44}} 1x, {{team.icon.image_88}} 2x\"\/>\n\t<div class=\"name_container overflow_ellipsis small_left_padding small_right_padding\">\n\t\t<div class=\"bold\">{{team.name}}<\/div>\n\t<\/div>\n\t<span class=\"medium\">{{team.active_members}} members<\/span>\n\t<i class=\"enter_icon ts_icon ts_icon_enter sky_blue not_in_token float_right small_top_margin auto_left_margin\"><\/i>\n<\/div>";
