TS.raw_templates['administration_items'] = "<li id=\"manage_team\" role=\"menuitem\">\n\t<a href=\"\/admin\" target=\"{{newWindowName}}\">Manage team members<\/a>\n<\/li>\n\n<li id=\"team_settings\" role=\"menuitem\">\n\t<a href=\"\/admin\/settings\" target=\"{{newWindowName}}\">Team settings<\/a>\n<\/li>\n\n{{#if show_org_settings}}\n\t<li id=\"org_settings\" role=\"menuitem\">\n\t\t<a href=\"\/\/{{enterprise_domain}}\" target=\"{{newWindowName}}\">Organization settings<\/a>\n\t<\/li>\n{{\/if}}\n";
TS.raw_templates['switch_teams_items'] = "{{#if other_enterprise_accounts}}\n\t{{#each other_enterprise_accounts}}\n\n\t\t{{#if @first}}{{else}}<li class=\"divider\"><\/li>{{\/if}}\n\n\t\t<div class=\"section_header\"><span class=\"header_label menu_mini\">{{this.enterprise_name}}<\/span><\/div>\n\n\t\t{{#unless ..\/is_our_app}}\n\t\t\t{{#each this.enterprise_teams}}\n\t\t\t\t<li class=\"switch_team\" data-action=\"switch_team\" data-team-id=\"{{this.team_id}}\" data-user-id=\"{{this.id}}\" role=\"menuitem\">\n\t\t\t\t\t<a href=\"{{this.team_url}}messages\" target=\"_self\">Switch to <strong>{{this.team_name}}<\/strong><\/a>\n\t\t\t\t<\/li>\n\t\t\t{{\/each}}\n\t\t\t{{#if needs_other_enterprise_logout}}\n\t\t\t\t<li data-action=\"enterprise_logout\" role=\"menuitem\"><a href=\"{{this.logout_url}}\" target=\"_self\">Sign out of <strong>{{this.enterprise_name}}<\/strong><\/a><\/li>\n\t\t\t{{\/if}}\n\t\t{{\/unless}}\n\t{{\/each}}\n{{\/if}}\n\n{{#if other_accounts}}\n\t<li class=\"divider\"><\/li>\n\n\t<div class=\"section_header\"><span class=\"header_label menu_mini\">Your other teams<\/span><\/div>\n\n\t{{#each other_accounts}}\n\t\t{{#if @first}}{{else}}<li class=\"divider subdivider\"><\/li>{{\/if}}\n\n\t\t<li class=\"switch_team\" data-action=\"switch_team\" data-team-id=\"{{this.team_id}}\" data-user-id=\"{{this.id}}\" role=\"menuitem\">\n\t\t\t<a href=\"{{this.team_url}}messages\" target=\"_self\">Switch to <strong>{{this.team_name}}<\/strong><\/a>\n\t\t<\/li>\n\t\t<li data-action=\"logout\" role=\"menuitem\"><a href=\"{{logout_url}}\" target=\"_self\">Sign out of <strong>{{this.team_name}}<\/strong><\/a><\/li>\n\t{{\/each}}\n{{\/if}}\n\n{{#if signin_url}}\n\t<li class=\"divider\"><\/li>\n\n\t<li id=\"add_team\" role=\"menuitem\"><a href=\"{{signin_url}}\"> Sign in to another team &hellip;<\/a><\/li>\n{{\/if}}\n";
TS.raw_templates['channel_manage_teams_dialog'] = "<div class=\"channel_manage_teams_header charcoal_grey larger_bottom_padding\">Manage teams for <strong>#{{model_ob.name}}<\/strong><\/div>\n\n<p>Manage which {{currentEnterpriseName}} teams can join the #{{model_ob.name}} channel, or make the channel accessible to all teams in the {{currentEnterpriseName}} organization.<\/p>\n\n<div class=\"share_with_whom bottom_margin larger_top_margin\">\n\t<label>Share this channel with<\/label>\n\n\t<label class=\"radio small_bottom_margin\"><input type=\"radio\" name=\"share_with\" value=\"all\" checked>All <span class=\"enterprise-name\">{{enterprise_info.name}}<\/span> teams<\/label>\n\t<label class=\"radio small_bottom_margin\"><input type=\"radio\" name=\"share_with\" value=\"specific\">Specific <span class=\"enterprise-name\">{{enterprise_info.name}}<\/span> teams<\/label>\n\n\t<div class=\"specific_team hidden\">\n\t\t<input type=\"text\" name=\"domains\" placeholder=\"Add teams\" data-validation=\"required\" data-validation-for=\"share_with_specific\">\n\t<\/div>\n<\/div>\n\n<div class=\"channel_manage_teams_buttons large_top_margin\">\n\t<button class=\"btn btn_outline dialog_cancel\" data-qa=\"channel_option_cancel\">Cancel<\/button>\n\t<button class=\"btn ladda-button dialog_go very_small_left_margin\" data-style=\"expand-right\" data-qa=\"channel_option_go\"><span class=\"ladda-label\">Ok<\/span><\/button>\n<\/div>\n";
TS.raw_templates['call_settings_desc'] = "<p class=\"subtle_silver small\">\n\t{{#if is_third_party}}\n\t\tThis app may require a separate account. <a href=\"https:\/\/tinyspeck.slack.com\/apps\/category\/At0EFT6869-communication\">See all calling apps<\/a> for Slack.\n\t{{else}}\n\t\tEnable team members to start <a href=\"https:\/\/get.slack.help\/hc\/en-us\/articles\/216771908\">audio calls<\/a> with up to fifteen teammates.\n\t\t{{#unless has_paid}}\n\t\t\t(Sharing a call in a channel or with multiple people is a feature available only to paid teams.)\n\t\t{{\/unless}}\n\t{{\/if}}\n<\/p>";
TS.raw_templates['developer_apps_event_row'] = "<tr class=\"no_padding {{weight}}\" data-event-type=\"{{event_name}}\" data-event-scope=\"{{scope}}\" data-table=\"{{table}}\">\n\t<td class=\"no_bottom_padding\">\n\t\t<a href=\"\/events\/{{event_name}}\" data-is-pending-removal=\"false\" target=\"_blank\" class=\"small overflow_ellipsis {{row_class}}\">{{event_name}}<\/a>\n\t<\/td>\n\t<td class=\"no_bottom_padding\">\n\t\t<p class=\"event_description small\">{{description}}<\/p>\n\t<\/td>\n\t{{#unless is_bot}}\n\t<td class=\"no_bottom_padding\">\n\t{{!-- We keep the scope but hide it for pending bot subscriptions, so that we are able to find, and update it in the filter select when it is removed. --}}\n\t\t<p class=\"event_scope small\">{{scope}}<\/p>\n\t<\/td>\n\t{{\/unless}}\n\t<td class=\"no_bottom_padding\">\n\t\t<div class=\"display_flex flex_direction_row_reverse no_wrap\">\n\t\t\t<ts_icon class=\"remove_event btn_icon ts_icon ts_icon_trash cursor_pointer\" aria-label=\"Delete\"><\/ts_icon>\n\t\t<\/div>\n\t<\/td>\n<\/tr>";
