TS.raw_templates['enterprise_security_auth_form'] = "<form data-enterprise-security-form>\n\t<div class=\"ent_content_header\">\n\t\t<div class=\"display_flex align_items_baseline\">\n\t\t\t<h1 class=\"ent_header ent_header_primary\">Authentication<\/h1>\n\t\t<\/div>\n\t<\/div>\n\t<div\n\t\tclass=\"alert alert_success medium_bottom_margin\n\t\t{{#unless sso_verified}}hidden{{\/unless}}\"\n\t\t{{#unless sso_verified}}aria-hidden=\"true\"{{\/unless}}\n\t\tdata-enterprise-security-auth-success-message>\n\t\t<ts-icon class=\"ts_icon_check_circle_o\"><\/ts-icon>\n\t\tSSO is enabled and working as expected\n\t<\/div>\n\n\t<div\n\t\tclass=\"alert alert_error medium_bottom_margin\n\t\t{{#unless saml_error}}hidden{{\/unless}}\"\n\t\t{{#unless saml_error}}aria-hidden=\"true\"{{\/unless}}\n\t\tdata-enterprise-security-auth-error-message>\n\t\t<ts-icon class=\"ts_icon_check_circle_o\"><\/ts-icon>\n\t\tDrat! There was a problem saving your settings{{#if idp}} in {{idp}}{{\/if}}.\n\t\t<button\n\t\t\tdata-save-sso-auth-button\n\t\t\ttype=\"button\"\n\t\t\tclass=\"btn_unstyle enterprise_button___link\">Try again?<\/button>\n\t<\/div>\n\n\t<h2 class=\"enterprise_security_auth_small_heading\">Single Sign-On configuration<\/h2>\n\t<div class=\"enterprise_security_auth_section\">\n\t\t<h2 class=\"enterprise_security_auth_heading\">SAML 2.0 Endpoint (HTTP)\n\t\t\t<span class=\"enterprise_security_auth_subheading\">Please provide your SAML 2.0 Endpoint.\n\t\t\t{{!-- hide this link until we have helpdesk for ent <a href=\"#\">Custom SAML Instructions<\/a>. --}}\n\t\t\t<\/span>\n\t\t<\/h2>\n\t\t<input type=\"text\" class=\"enterprise_security_auth_input_small\" name=\"sso_url\" placeholder=\"http:\/\/company-app.sso-provider.com\/apps\/company\/123\" value=\"{{sso_url}}\">\n\t<\/div>\n\n\t<div class=\"enterprise_security_auth_section\">\n\t\t<h2 class=\"enterprise_security_auth_heading\">Identity Provider Issuer <span class=\"enterprise_security_auth_subtle\">(Optional)<\/span>\n\t\t\t<span class=\"enterprise_security_auth_subheading\">Your Identity Provider's entity ID.<\/span>\n\t\t<\/h2>\n\t\t<input type=\"text\" class=\"enterprise_security_auth_input_small\" name=\"idp_entity_id\" placeholder=\"http:\/\/company-app.sso-provider.com\/apps\/company\" value=\"{{idp_entity_id}}\">\n\t<\/div>\n\n\t<div class=\"enterprise_security_auth_section\">\n\t\t<h2 class=\"enterprise_security_auth_heading\">Public Certificate\n\t\t\t<span class=\"enterprise_security_auth_subheading\">Copy and paste your entire x.509 Certificate here.<\/span>\n\t\t<\/h2>\n\t\t<textarea name=\"pub_cert\">{{pub_cert}}<\/textarea>\n\t<\/div>\n\n\t<div class=\"enterprise_security_auth_section\">\n\t\t<label for=\"sync_with_provider\" class=\"checkbox enterprise_security_auth_label\">\n\t\t\t<input type=\"checkbox\" id=\"sync_with_provider\" name=\"sync_with_provider\" value=\"1\" {{#if sync_with_provider}}checked{{\/if}}>\n\t\t\tUpdate profile each time a user logs in\n\t\t<\/label>\n\t\t<p class=\"enterprise_security_auth_description\">Available user profile fields will be synced from your identity provider every time a user logs in.<\/p>\n\t<\/div>\n\n\t<div class=\"enterprise_security_auth_section\">\n\t\t<label for=\"change_email\" class=\"checkbox enterprise_security_auth_label\">\n\t\t\t<input type=\"checkbox\" id=\"change_email\" name=\"change_email\" value=\"1\" {{#if change_email}}checked{{\/if}}>\n\t\t\tAllow users to change their email address\n\t\t<\/label>\n\t\t<p class=\"enterprise_security_auth_description\">\n\t\t\tUsers will be able to change their email address to something other than the one from their {{idp}} account. They'll still log in with {{idp}}.\n\t\t<\/p>\n\t<\/div>\n\n\t<div class=\"enterprise_security_auth_section\">\n\t\t<label for=\"choose_username\" class=\"checkbox enterprise_security_auth_label\">\n\t\t\t<input type=\"checkbox\" id=\"choose_username\" name=\"choose_username\" value=\"1\" {{#if choose_username}}checked{{\/if}}>\n\t\t\tAllow users to change their username\n\t\t<\/label>\n\t\t<p class=\"enterprise_security_auth_description\">\n\t\t\tUsers will be able to change their username to something other than the one from their {{idp}} account.\n\t\t<\/p>\n\t<\/div>\n\n\t<div class=\"enterprise_security_auth_section\">\n\t\t<label for=\"auth_restrictions\"\n\t\t\tclass=\"select enterprise_security_auth_select_small\">\n\t\t\tAuthentication must be used by:\n\t\t\t<select id=\"auth_restrictions\" name=\"auth_restrictions\">\n\t\t\t\t<option value=\"0\"\n\t\t\t\t\t{{#if_equal auth_restrictions compare=\"0\"}}selected{{\/if_equal}}>\n\t\t\t\t\tAll org members\n\t\t\t\t<\/option>\n\t\t\t\t<option value=\"1\"\n\t\t\t\t\t{{#if_equal auth_restrictions compare=\"1\"}}selected{{\/if_equal}}>\n\t\t\t\t\tAll org members except guest accounts\n\t\t\t\t<\/option>\n\t\t\t<\/select>\n\t\t<\/label>\n\t<\/div>\n\n\t{{#feature flag=\"feature_enterprise_mandatory_2fa\"}}\n\t\t<hr class=\"enterprise_security_auth_section_divider\">\n\n\t\t{{!-- alerts - these were a component but there were render issue and putthing them here is more logical for hte application. --}}\n\t\t{{#if_equal mandatory_2fa_alert compare=\"success\"}}\n\t\t\t<div class=\"alert alert_success\" data-enterprise-mandatory-2fa-success>\n\t\t\t\t<ts-icon class=\"ts_icon_check_circle_o\"><\/ts-icon>\n\t\t\t\tSuccess! You've {{#if_equal mandatory_2fa_alert_type compare=\"disable\"}}disabled{{else if_equal mandatory_2fa_alert_type compare=\"enable\"}}enabled{{\/if_equal}} 2FA for your organization.\n\t\t\t<\/div>\n\t\t{{\/if_equal}}\n\t\t{{#if_equal mandatory_2fa_alert compare=\"error\"}}\n\t\t\t<div class=\"alert alert_error\" data-enterprise-mandatory-2fa-error>\n\t\t\t\t<ts-icon class=\"ts_icon_warning\"><\/ts-icon>\n\t\t\t\tHmm, that didn't work. Please try {{#if_equal mandatory_2fa_alert_type compare=\"disable\"}}disabling{{else if_equal mandatory_2fa_alert_type compare=\"enable\"}}enabling{{\/if_equal}} 2FA again.\n\t\t\t<\/div>\n\t\t{{\/if_equal}}\n\n\n\t\t<div class=\"enterprise_security_auth_section\">\n\t\t\t<h2 class=\"enterprise_security_auth_small_heading\">Mandatory 2fa<\/h2>\n\t\t\t<p class=\"enterprise_security_auth_description bottom_margin\">Enable mandatory two-factor authentication (2FA) to require organization owners and guests to sign in more securely.<\/p>\n\t\t\t<button class=\"btn btn_outline btn_large\" {{#if two_factor_is_on}}data-org-disable-2fa-modal{{else}}data-org-2fa-modal{{\/if}} >{{#if two_factor_is_on}}Disable 2FA{{else}}Turn on 2FA{{\/if}}<\/button>\n\t\t<\/div>\n\t{{\/feature}}\n\n\t<hr class=\"enterprise_security_auth_section_divider\">\n\n\t<h2 class=\"enterprise_security_auth_small_heading\">Session Duration<\/h2>\n\t<p class=\"enterprise_security_auth_description bottom_margin\">Limiting session durations requires team members to re-authenticate periodically. This can help increase security and prevent unauthorized access to your team.<\/p>\n\n\t<label for=\"session_duration\" class=\"validation_error\">\n\t\t<span\n\t\t\tdata-ent-session-duration-error\n\t\t\tclass=\"validation_message overflow_ellipsis inline\"\n\t\t\ttitle=\"\">\n\t\t<\/span>\n\t<\/label>\n\t<div class=\"enterprise_security_auth_session_duration\" data-ent-security-auth-session-duration>\n\t\t<div class=\"enterprise_security_auth_session_duration_type_container small_right_padding\">\n\t\t\t<label for=\"session_duration_type\" class=\"select enterprise_security_auth_select_small\">\n\t\t\t\t<select id=\"session_duration_type\" name=\"session_duration_type\">\n\t\t\t\t\t<option {{#if_equal session_duration_type compare=\"-1\"}}selected{{\/if_equal}} value=\"-1\">Allow infinite sessions<\/option>\n\t\t\t\t\t<option {{#if_equal session_duration_type compare=\"0\"}}selected{{\/if_equal}} value=\"0\">Log users out when they close the application<\/option>\n\t\t\t\t\t<option {{#if_equal session_duration_type compare=\"1\"}}selected{{\/if_equal}} value=\"1\">Log users out after a specific amount of hours (minimum 12)<\/option>\n\t\t\t\t<\/select>\n\t\t\t<\/label>\n\t\t<\/div>\n\t\t<div\n\t\t\tclass=\"enterprise_security_auth_session_duration_container\n\t\t\t\t\t\t {{#if_lt session_duration_type compare=1}}hidden{{\/if_lt}}\">\n\t\t\t<span class=\"input-append no_bottom_margin\">\n\t\t\t\t<input\n\t\t\t\t\ttype=\"text\"\n\t\t\t\t\tname=\"session_duration\"\n\t\t\t\t\tclass=\"align_right retention_duration_input\"\n\t\t\t\t\tvalue=\"{{session_duration}}\">\n\t\t\t\t<span class=\"small_left_padding small_bottom_padding\">hours<\/span>\n\t\t\t<\/span>\n\t\t<\/div>\n\t<\/div>\n<\/form>\n\n<form data-enterprise-reset-password-form>\n\t<hr class=\"enterprise_security_auth_section_divider\">\n\n\t<div data-enterprise-reset-password-alerts>\n\t\t<div class=\"alert alert_success hidden\" data-enterprise-reset-password-success>\n\t\t\t<ts-icon class=\"ts_icon_check_circle_o\"><\/ts-icon>\n\t\t\tSuccess! You've forced a password reset for your organization.\n\t\t<\/div>\n\n\t\t<div class=\"alert alert_error hidden\" data-enterprise-reset-password-error>\n\t\t\t<ts-icon class=\"ts_icon_warning\"><\/ts-icon>\n\t\t\tHmm, that didn't work. Please try forcing the password reset again.\n\t\t<\/div>\n\t<\/div>\n\n\t<h2 class=\"enterprise_security_auth_small_heading\">Forced Password Reset<\/h2>\n\t<p class=\"enterprise_security_auth_description bottom_margin\">You can force a password reset for all team members (if, for example, you suspect someone has gained access to team passwords). Reminder: this will only reset passwords for team members who don't sign in with {{#if idp}}{{idp}}{{else}}SSO{{\/if}}.<\/p>\n\n\t<p class=\"enterprise_security_auth_description bottom_margin\">\n\t\tIn order to create a new password, each team member must be able to receive the email we send with the reset link. <span>(<a data-manage-mail-servers-alert >Manage your own mail servers?<\/a>)<\/span>\n\t<\/p>\n\t<div data-email-ip-list class=\"alert alert_info hidden\">\n\t\t<p>\n\t\t\t<i class=\"ts_icon ts_icon_info_circle\"><\/i> If you manage your own mail servers, to help ensure that all emails arrive promptly, please whitelist these IPs:<br><br>\n\t\t\t166.78.71.234 \/ 166.78.71.235 \/ 198.61.254.87 \/ 166.78.71.183 \/ 166.78.70.132 \/ 184.173.153.232 \/ 184.173.105.177 \/ 184.173.105.179 \/ 166.78.69.28 \/ 166.78.69.26 \/ 166.78.69.32 \/ 166.78.69.35 \/ 166.78.69.37\n\t\t<\/p>\n\t<\/div>\n\n\t<div class=\"enterprise_security_auth_password_reset\">\n\t\t<div class=\"enterprise_security_auth_password_reset_container\">\n\t\t\t<label for=\"password_reset_type\" class=\"select enterprise_security_auth_select_small small_right_padding\">\n\t\t\t\t<select id=\"password_reset_type\" name=\"password_reset_type\" data-enterprise-reset-password-select>\n\t\t\t\t\t<option value=\"0\" selected=\"true\" disabled=\"true\" data-select-default>Please Choose...<\/option>\n\t\t\t\t\t<option value=\"force_logout_true\">Force password reset and log all members out of Slack<\/option>\n\t\t\t\t\t<option value=\"force_logout_false\">Force password reset<\/option>\n\t\t\t\t<\/select>\n\t\t\t<\/label>\n\n\t\t\t<button type=\"submit\" class=\"enterprise_security_auth_button_for_select btn btn_large btn_danger\" data-enterprise-reset-password-submit disabled=\"true\">Force Reset<\/button>\n\t\t<\/div>\n\t<\/div>\n<\/form>\n\n<form data-enterprise-advanced-sso-options-form>\n\t<hr class=\"enterprise_security_auth_section_divider\">\n\n\t<h2 class=\"enterprise_security_auth_small_heading\">Advanced Options<\/h2>\n\n\t<div class=\"enterprise_security_auth_section\">\n\t\t<h2 class=\"enterprise_security_auth_heading\">Service Provider Issuer\n\t\t\t<span class=\"enterprise_security_auth_subheading\">The sP Entity ID you would like us to send. By default, this is <strong>https:\/\/slack.com<\/strong><\/span>\n\t\t<\/h2>\n\t\t<input type=\"text\" class=\"enterprise_security_auth_input_small full_width\" name=\"sp_entity_id\" placeholder=\"https:\/\/slack.com\" value=\"{{sp_entity_id}}\">\n\t<\/div>\n<\/form>\n";
TS.raw_templates['enterprise_security_auth_enable_org_2fa'] = "<div class=\"enterprise_security_auth_2fa_modal\">\n\t<p>Thanks for keeping your org's accounts safe and secure with  <a href=\"https:\/\/slack.zendesk.com\/hc\/en-us\/articles\/204509068-Enabling-two-factor-authentication\" target=\"_blank\">two-factor<\/a> authentication (2FA). Next, all team members who don't use SSO and don't yet have 2FA set up will receive a Slackbot notification and an email about how to get set up.<\/p>\n\t\n\t<p class=\"large_bottom_margin\">Team members who don't set up 2FA within 24 hours will be logged out and prompted to set up 2FA before logging in again.<\/p>\n\n\t<p class=\"bottom_margin\"><strong><a id=\"customize_toggle\" class=\"indifferent_grey\" href=\"#\">Customize Slackbot Message <span class=\"custom_slackbot_message__caret open\"><ts-icon class=\"ts_icon_caret_right\"><\/ts-icon><\/span><\/a><\/strong><\/p>\n\t<div id=\"customize\" class=\"customize_wrapper\">\n\t\t<form action=\"\/admin\/auth\/2fa\" method=\"post\">\n\t\t\t<div class=\"accordion_subsection display_none\">\n\t\t\t\t<div class=\"message_wrapper\">\n\t\t\t\t\t<div class=\"slackbot_message___container message show_user avatar first divider\">\n\t\t\t\t\t\t<img class=\"slackbot_message_preview__slackbot_icon\" src=\"https:\/\/slack-assets2.s3-us-west-2.amazonaws.com\/10068\/img\/slackbot_48.png\" aria-hidden=\"true\">\n\t\t\t\t\t\t<div class=\"slackbot_message__contents\">\n\t\t\t\t\t\t\t<div class=\"message_content slackbot_message_preview__text\">\n\t\t\t\t\t\t\t\t<p class=\"message_sender member lato_bold color_USLACKBOT no_margin\">slackbot<\/p>\n\t\t\t\t\t\t\t\t<p class=\"no_margin\">{{enable_2fa_message}}<\/p>\n\t\t\t\t\t\t\t\t<p class=\"message_content custom_message top_margin subtle_silver\" id=\"custom_message\" data-qa=\"custom_message_placeholder\">Your custom message goes here.<\/p>\n\t\t\t\t\t\t\t<\/div>\n\t\t\t\t\t\t<\/div>\n\t\t\t\t\t<\/div>\n\t\t\t\t<\/div>\n\t\t\t\t<input type=\"hid