TS.raw_templates['enterprise_settings_team_settings_realname_policy'] = "<div class=\"org_policy_modal\">\n\t<h3 class=\"org_policy_modal__content_header lato_bold\">Username\/Real Name Display Policy<\/h3>\n\n\t<p class=\"org_policy_modal__content_subheader\">By default, Slack displays members' usernames: short nicknames which are all lowercase and cannot contain spaces. You can instead choose to set your org's default to display members' real first and last names.<\/p>\n\n\t<form class=\"org_policy_modal__content_options org_policy_form full_width\" data-form-policy=\"display_real_names\">\n\t\t<label class=\"select large org_policy_modal__content_select full_width\">\n\t\t\t<select name=\"display_real_names\" class=\"large full_width\">\n\t\t\t\t<option value=\"0\" {{#unless display_real_names}}selected{{\/unless}}>Display usernames for team members by default<\/option>\n\t\t\t\t<option value=\"1\" {{#if display_real_names}}selected{{\/if}}>Display real first &amp; last names for team members by default<\/option>\n\t\t\t<\/select>\n\t\t<\/label>\n\t<\/form>\n<\/div>\n\n<div class=\"hidden org_policy_modal_overlay_container\" data-modal-overlay data-enterprise-settings-confirm-policy-overlay>\n{{> enterprise_settings_confirm_policy}}\n<\/div>\n";
TS.raw_templates['enterprise_settings_team_settings_at_mention_policy'] = "<div class=\"org_policy_modal\">\n\t<h3 class=\"org_policy_modal__content_header lato_bold\">Require &quot;@&quot; for mentions<\/h3>\n\n\t<p class=\"org_policy_modal__content_subheader\">By default, Slack highlights mentions to a team member when a message includes their username \u2014 with or without an &quot;@&quot; sign prefix. You can require that the &quot;@&quot; sign be included in order to trigger a mention.<\/p>\n\n\t<div class=\"alert alert_info\">\n\t\t<ts-icon class=\"ts_icon_info_circle\"><\/ts-icon>\n\t\tTeam members can add their username to their highlight words to override this preference.\n\t<\/div>\n\t<div class=\"alert alert_info\">\n\t\t<ts-icon class=\"ts_icon_info_circle\"><\/ts-icon>\n\t\tMobile support for this preference is coming soon!\n\t<\/div>\n\t<form class=\"org_policy_modal__content_options org_policy_form\" data-form-policy=\"require_at_for_mention\">\n\t\t<label class=\"large org_policy_modal__content_select\" for=\"require_at_for_mention\">\n\t\t\t<input type=\"checkbox\" id=\"require_at_for_mention\" name=\"require_at_for_mention\" {{#if require_at_for_mention}}checked=\"checked\" aria-checked=\"true\"{{\/if}} > Require &quot;@&quot; prefix for user mentions\n\t\t<\/label>\n\t<\/form>\n<\/div>\n\n<div class=\"hidden org_policy_modal_overlay_container\" data-modal-overlay data-enterprise-settings-confirm-policy-overlay>\n{{> enterprise_settings_confirm_policy}}\n<\/div>\n";
TS.raw_templates['enterprise_settings_team_settings_referrer_policy'] = "<div class=\"org_policy_modal\">\n\t<h3 class=\"org_policy_modal__content_header lato_bold\">Hide referrer information for external links<\/h3>\n\n\t<p class=\"org_policy_modal__content_subheader\">When team members post links to external sites, you can choose to prevent those sites from tracking clicks back to your team site. If in doubt, we recommend hiding your team\u2019s information.<\/p>\n\n\t<form class=\"org_policy_modal__content_options org_policy_form\" data-form-policy=\"hide_referers\">\n\t\t<label class=\"large\" for=\"hide_referers\">\n\t\t\t<input type=\"checkbox\" id=\"hide_referers\" name=\"hide_referers\" {{#if hide_referers}}checked=\"checked\" aria-checked=\"true\"{{\/if}} > Run external links through a blind redirect to hide referrer information\n\t\t<\/label>\n\t<\/form>\n<\/div>\n\n<div class=\"hidden org_policy_modal_overlay_container\" data-modal-overlay data-enterprise-settings-confirm-policy-overlay>\n{{> enterprise_settings_confirm_policy}}\n<\/div>\n";
TS.raw_templates['enterprise_settings_team_settings_dnd_policy'] = "<div class=\"org_policy_modal\">\n\t<h3 class=\"org_policy_modal__content_header lato_bold\">Require Default Do Not Disturb Settings<\/h3>\n\n\t<p class=\"org_policy_modal__content_subheader\">Set default Do Not Disturb hours for every team in your organization. When Do Not Disturb is enabled, Slack will not send desktop, mobile, or email notifications. Changing it here will reset everyone to your selections, though DND hours will remain customizable to team members.<\/p>\n\n\t<form class=\"org_policy_modal__content_options org_policy_form\" data-form-policy=\"require_at_for_mention\">\n\t\t<label class=\"checkbox\">\n\t\t\t<input\n\t\t\t\ttype=\"checkbox\"\n\t\t\t\tname=\"dnd_enabled\"\n\t\t\t\tvalue=\"1\"\n\t\t\t\tid=\"dnd_enabled\"\n\t\t\t\t{{#if dnd_enabled}}\n\t\t\t\t\tchecked\n\t\t\t\t{{\/if}}>\n\t\t\tAutomatically disable notifications from:\n\t\t<\/label>\n\n\t\t<label class=\"select small inline_block\">\n\t\t\t<select\n\t\t\t\tname=\"dnd_start_hour\"\n\t\t\t\tid=\"dnd_start_hour\"\n\t\t\t\tclass=\"small auto_width\"\n\t\t\t\t{{#unless dnd_enabled}}\n\t\t\t\t\tdisabled\n\t\t\t\t{{\/unless}}>\n\t\t\t\t{{#each times}}\n\t\t\t\t\t<option\n\t\t\t\t\t\tvalue=\"{{this.value}}\"\n\t\t\t\t\t\t{{#if_equal this.value compare=..\/dnd_start_hour}}selected{{\/if_equal}}\n\t\t\t\t\t\t{{#if_equal this.value compare=..\/dnd_end_hour}}disabled{{\/if_equal}}\n\t\t\t\t\t>\n\t\t\t\t\t\t{{this.label}}\n\t\t\t\t\t<\/option>\n\t\t\t\t{{\/each}}\n\t\t\t<\/select>\n\t\t<\/label>\n\t\t<span class=\"small_left_margin small_right_margin\">to<\/span>\n\t\t<label class=\"select small inline_block\">\n\t\t\t<select\n\t\t\t\tname=\"dnd_end_hour\"\n\t\t\t\tid=\"dnd_end_hour\"\n\t\t\t\tclass=\"small auto_width\"\n\t\t\t\t{{#unless dnd_enabled}}\n\t\t\t\t\tdisabled\n\t\t\t\t{{\/unless}}>\n\t\t\t\t{{#each times}}\n\t\t\t\t\t<option\n\t\t\t\t\t\tvalue=\"{{this.value}}\"\n\t\t\t\t\t\t{{#if_equal this.value compare=..\/dnd_end_h