TS.raw_templates['enterprise_settings_team_settings_fileretention_policy'] = "<div class=\"org_policy_modal\">\n\t<h3 class=\"org_policy_modal__content_header lato_bold\">File Retention Policy<\/h3>\n\n\t<p class=\"org_policy_modal__content_subheader\">You can change how long Slack saves your files (by default, Slack saves files for the lifetime of your team).<\/p>\n\n\t<form\n\t\tclass=\"org_policy_modal__content_options org_policy_form\"\n\t\tdata-form-policy=\"file_retention\">\n\t\t<div class=\"ent_select_and_input_group\">\n\t\t\t<label class=\"select large org_policy_modal__content_select\">\n\t\t\t\tFile Retention\n\t\t\t\t<select name=\"file_retention_type\" class=\"large\">\n\t\t\t\t\t<option\n\t\t\t\t\t\tvalue=\"0\"\n\t\t\t\t\t\t{{#if_equal file_retention_type compare=\"0\"}}selected{{\/if_equal}}>\n\t\t\t\t\t\tRetain all files\n\t\t\t\t\t<\/option>\n\t\t\t\t\t<option\n\t\t\t\t\t\tvalue=\"1\"\n\t\t\t\t\t\t{{#if_equal file_retention_type compare=\"1\"}}selected{{\/if_equal}}>\n\t\t\t\t\t\tRetain all files for only a specific number of days\n\t\t\t\t\t<\/option>\n\t\t\t\t<\/select>\n\t\t\t<\/label>\n\n\t\t\t<label\n\t\t\t\tclass=\"ent_policy_file_retention__input_label\n\t\t\t\t\t\t\t{{#unless file_retention_duration}}hidden{{\/unless}}\"\n\t\t\t\tfor=\"files_retention_duration\"\n\t\t\t\tdata-duration-label=\"files_retention\">\n\t\t\t\tDuration\n\t\t\t\t<input\n\t\t\t\t\ttype=\"text\"\n\t\t\t\t\tclass=\"ent_policy_file_retention__input\"\n\t\t\t\t\tname=\"files_retention_duration\"\n\t\t\t\t\tid=\"files_retention_duration\"\n\t\t\t\t\tdata-duration-value\n\t\t\t\t\t{{#if file_retention_duration}}value=\"{{file_retention_duration}}\"{{\/if}}>\n\t\t\t<\/label>\n\t\t\t<label\n\t\t\t\tclass=\"select\n\t\t\t\t\t\t\tlarge\n\t\t\t\t\t\t\torg_policy_file_retention__content_select\n\t\t\t\t\t\t\t{{#unless file_retention_duration}}hidden{{\/unless}}\">\n\t\t\t\t<select name=\"file_retention_time_unit\" class=\"large\">\n\t\t\t\t\t<option value=\"0\">Days<\/option>\n