TS.raw_templates['enterprise_settings_team_settings_calls_policy'] = "<div class=\"org_policy_modal\">\n\t<h3 class=\"org_policy_modal__content_header lato_bold\">Calls Policy<\/h3>\n\n\t<p class=\"org_policy_modal__content_subheader\">Once enabled, members of your organization will be able to start audio calls with up to 15 teammates in any channel, direct message, or group direct message.<\/p>\n\n\t<form class=\"org_policy_modal__content_options org_policy_form\" data-form-policy=\"allow_calls\">\n\t\t<p class=\"alert\">\n\t\t\t<ts-icon class=\"ts_icon_warning\"><\/ts-icon>\n\t\t\tCurrently on Mac and Windows desktop apps, Chrome and the iOS app; coming soon to Android!\n\t\t<\/p>\n\t\t<label for=\"allow_calls\" class=\"checkbox\">\n\t\t\t<input type=\"checkbox\" name=\"allow_calls\" value=\"1\" id=\"allow_calls\" {{#if allow_calls}}checked aria-checked=\"true\"{{\/if}}>\n\t\t\tEnable Calls for all teams\n\t\t<\/label>\n\t<\/form>\n<\/div>\n\n<div class=\"hidden org_policy_modal_overlay_container\" data-modal-overlay data-enterprise-settings-confirm-policy-overlay>\n{{> enterprise_settings_confirm_policy}}\n<\/div>";
TS.raw_templates['enterprise_settings_team_settings_message_retention_policy'] = "<div class=\"org_policy_modal\">\n\t<h3 class=\"org_policy_modal__content_header lato_bold\">Message Retention<\/h3>\n\n\t<p class=\"org_policy_modal__content_subheader\">By default, Slack will save your message data for the lifetime of your team. You can change how long Slack saves your messages.<\/p>\n\n\t<div class=\"alert alert_warning fontsize_ninetenthsrem large_bottom_margin\">\n\t\t<ts-icon class=\"ts_icon_warning\"><\/ts-icon>\n\t\tNote that Message Retention policies do not apply to files your team uploads to Slack. Admins and file owners can delete files directly in the File Listing.\n\t<\/div>\n\n\t<form class=\"org_policy_modal__content_options org_policy_form\" data-form-policy=\"message_retention\">\n\n\t\t<div class=\"ent_select_and_input_group large_bottom_margin flex_wrap\">\n\t\t\t<label class=\"select large org_policy_modal__content_select eighteen_px_font ent_select_and_input_group__select_label\" for=\"retention\">\n\t\t\t\tChannel Retention\n\t\t\t\t<select name=\"retention\" id=\"retention\" class=\"large eighteen_px_font ent_select_and_input_group__select\" data-retention-select>\n\t\t\t\t\t<option value=\"0\" {{#if_equal retention_type compare=\"0\"}}selected{{\/if_equal}}>Retain all messages<\/option>\n\t\t\t\t\t<option value=\"1\" {{#if_equal retention_type compare=\"1\"}}selected{{\/if_equal}}>Retain all messages AND retain edit\/deletion logs for all messages<\/option>\n\t\t\t\t\t<option value=\"2\" {{#if_equal retention_type compare=\"2\"}}selected{{\/if_equal}}>Retain messages and logs for only a specific number of days<\/option>\n\t\t\t\t\t<option value=\"3\" {{#if_equal retention_type compare=\"3\"}}selected{{\/if_equal}}>Retain messages for only a specific number of days (but keep logs for compliance exports)<\/option>\n\t\t\t\t<\/select>\n\t\t\t<\/label>\n\t\t\t<label class=\"ent_select_and_input_group__input_label {{#unless retention_duration}}hidden{{\/unless}}\" for=\"retention_duration\" data-duration-label=\"retention\">\n\t\t\t\t<span class=\"block medium\">Duration<\/span>\n\n\t\t\t\t<input type=\"text\" maxlength=\"4\" class=\"ent_select_and_input_group__input\" name=\"retention_duration\" id=\"retention_duration\" data-duration-value {{#if retention_duration}}value=\"{{retention_duration}}\"{{\/if}}>\n\t\t\t\t<span class=\"ent_select_and_input_group__affordance\">days<\/span>\n\t\t\t<\/label>\n\t\t\t<div class=\"ent_select_and_input_group__error\" data-error-retention><\/div>\n\t\t<\/div>\n\t\t<div class=\"ent_select_and_input_group large_bottom_margin flex_wrap\">\n\t\t\t<label class=\"select large org_policy_modal__content_select eighteen_px_font ent_select_and_input_group__select_label\" for=\"group_retention\">\n\t\t\t\tPrivate Channel Retention\n\t\t\t\t<select name=\"group_retention\" id=\"group_retention\" class=\"large eighteen_px_font ent_select_and_input_group__select\" data-retention-select>\n\t\t\t\t\t<option value=\"0\" {{#if_equal group_retention_type compare=\"0\"}}selected{{\/if_equal}}>Retain all messages<\/option>\n\t\t\t\t\t<option value=\"1\" {{#if_equal group_retention_type compare=\"1\"}}selected{{\/if_equal}}>Retain all messages AND retain edit\/deletion logs for all messages<\/option>\n\t\t\t\t\t<option value=\"2\" {{#if_equal group_retention_type compare=\"2\"}}selected{{\/if_equal}}>Retain messages and logs for only a specific number of days<\/option>\n\t\t\t\t\t<option value=\"3\" {{#if_equal group_retention_type compare=\"3\"}}selected{{\/if_equal}}>Retain messages for only a specific number of days (but keep logs for compliance exports)<\/option>\n\t\t\t\t<\/select>\n\t\t\t<\/label>\n\t\t\t<label class=\"ent_select_and_input_group__input_label {{#unless group_retention_duration}}hidden{{\/unless}}\" for=\"group_retention_duration\" data-duration-label=\"group_retention\">\n\t\t\t\t<span class=\"block medium\">Duration<\/span>\n\t\t\t\t<input type=\"text\" maxlength=\"4\" class=\"ent_select_and_input_group__input\" name=\"group_retention_duration\" id=\"group_retention_duration\" data-duration-value {{#if group_retention_duration}}value=\"{{group_retention_duration}}\"{{\/if}}>\n\t\t\t\t<span class=\"ent_select_and_input_group__affordance\">days<\/span>\n\t\t\t<\/label>\n\t\t\t<div class=\"ent_select_and_input_group__error\" data-error-group_retention><\/div>\n\t\t<\/div>\n\n\t\t<div class=\"ent_select_and_input_group large_bottom_margin flex_wrap\">\n\t\t\t<label class=\"select large org_policy_modal__content_select eighteen_px_font  ent_select_and_input_group__select_label\" for=\"dm_retention\">\n\t\t\t\tDirect Message Retention\n\t\t\t\t<select name=\"dm_retention\" id=\"dm_retention\" class=\"large eighteen_px_font ent_select_and_input_group__select\" data-retention-select>\n\t\t\t\t\t<option value=\"0\" {{#if_equal dm_retention_type compare=\"0\"}}selected{{\/if_equal}}>Retain all messages<\/option>\n\t\t\t\t\t<option value=\"1\" {{#if_equal dm_retention_type compare=\"1\"}}selected{{\/if_equal}}>Retain all messages AND retain edit\/deletion logs for all messages<\/option>\n\t\t\t\t\t<option value=\"2\" {{#if_equal dm_retention_type compare=\"2\"}}selected{{\/if_equal}}>Retain messages and logs for only a specific number of days<\/option>\n\t\t\t\t\t<option value=\"3\" {{#if_equal dm_retention_type compare=\"3\"}}selected{{\/if_equal}}>Retain messages for only a specific number of days (but keep logs for compliance exports)<\/option>\n\t\t\t\t<\/select>\n\t\t\t<\/label>\n\t\t\t<label class=\"ent_select_and_input_group__input_label {{#unless dm_retention_duration}}hidden{{\/unless}}\" for=\"dm_retention_duration\" data-duration-label=\"dm_retention\">\n\t\t\t\t<span class=\"block medium\">Duration<\/span>\n\t\t\t\t<input type=\"text\" maxlength=\"4\" class=\"ent_select_and_input_group__input\" name=\"dm_retention_duration\" id=\"dm_retention_duration\" data-duration-value {{#if dm_retention_duration}}value=\"{{dm_retention_duration}}\"{{\/if}}>\n\t\t\t\t<span class=\"ent_select_and_input_group__affordance\">days<\/span>\n\t\t\t<\/label>\n\t\t\t<div class=\"ent_select_and_input_group__error\" data-error-dm_retention><\/div>\n\t\t<\/div>\n\n\t\t<label class=\"large large_bottom_margin\" for=\"allow_retention_override\">\n\t\t\t<input type=\"checkbox\" id=\"allow_retention_override\" name=\"allow_retention_override\" {{#if allow_retention_override}}checked=\"checked\" aria-checked=\"true\"{{\/if}} > Allow team members to set retention policies for channels and direct message conversations that override the team-wide policies\n\t\t<\/label>\n\t<\/form>\n\n\t<div class=\"alert alert_warning fontsize_ninetenthsrem\">\n\t\t<ts-icon class=\"ts_icon_warning\"><\/ts-icon>\n\t\t<strong>An important note about custom message retention policies<\/strong><br>\n\t\tSetting a custom duration for message retention means that messages in your team's archives will be deleted after the number of days you set. This deletion is permanent and the messages will be irretrievable. Please proceed with caution!\n\t<\/div>\n\n<\/div>\n\n<div class=\"hidden org_policy_modal_overlay_container\" data-modal-overlay data-enterprise-settings-confirm-policy-overlay>\n{{> enterprise_settings_confirm_policy_message_retention}}\n<\/div>\n";
TS.raw_templates['enterprise_settings_shared_channel_restrictions'] = "<div class=\"enterprise_shared_channels_settings\">\n\t<div class=\"ent_content_header\">\n\t\t<div class=\"display_flex align_items_baseline\">\n\t\t\t<h1 class=\"ent_header ent_header_primary\">Shared Channel Settings<\/h1>\n\t\t<\/div>\n\t<\/div>\n\t{{! Management for shared channels should be hidden for now, as the current spec demands, is fully operational but will be hidden till it is ready to be released }}\n\t{{! <p class=\"large_top_margin inline_block uppercase permissions_title\" id=\"manage_prefs_shared_channels\">Shared Channel Management<\/p>\n\t<p class=\"permissions_title_info\">These members can <b>create<\/b>, <b>modify<\/b>, or <b>convert<\/b> shared channels:<\/p>\n\t<div data-name=\"enterprise_manage_shared_radios\"><\/div>\n\t<div id=\"lfs_manage_selector\" class=\"hidden\">\n\t<input id=\"lfs_manage\" class=\"no_bottom_margin\" \/>\n\t<\/div>\n\t<hr class=\"tiny_bottom_margin\"> }}\n\t<p class=\"inline_block uppercase permissions_title\" id=\"posting_prefs_shared_channels\"> Posting Privileges <\/p>\n\t<p class=\"permissions_title_info\">These members can <b>post<\/b> into a shared channel:<\/p>\n\t<div data-name=\"enterprise_post_shared_radios\"><\/div>\n\t<div id=\"lfs_post_selector\" class=\"hidden left_margin\">\n\t\t<input id=\"lfs_post\" class=\"no_bottom_margin\" \/>\n\t<\/div>\n\t<hr class=\"top_margin very_small_bottom_margin\">\n\t<label class=\"radio modify_options override_box\">\n\t\t<input name=\"allow_shared_channel_perms_override\" type=\"checkbox\" value=true data-inline-saver=\"#prefs_shared_channels\" id=\"override_fs\"\n\t\t{{#if_equal allow_shared_channel_perms_override compare=true}}\n\t\tchecked=\"checked\"{{\/if_equal}}>\n\t\tAllow team admins to set posting privileges for shared channels that override the org-wide setting.\n\t<\/label>\n<\/div>\n";
TS.raw_templates['enterprise_settings_shared_channel_restrictions_post_shared_radios'] = "<label class=\"radio modify_options\">\n\t<input name=\"who_can_post_in_shared_channels\" type=\"radio\" value=\"ra\" data-inline-saver=\"#prefs_shared_channels\" class=\"post_opt_radios\"\n\t{{#if_equal who_can_post_in_shared_channels.type compare=\"ra\"}}\n\tchecked=\"checked\"{{\/if_equal}}>\n\tEveryone\n<\/label>\n<label class=\"radio modify_options\">\n\t<input name=\"who_can_post_in_shared_channels\" type=\"radio\" value=\"regular\" data-inline-saver=\"#prefs_shared_channels\" class=\"post_opt_radios\"\n\t{{#if_equal who_can_post_in_shared_channels.type compare=\"regular\"}}\n\tchecked=\"checked\"{{\/if_equal}}>\n\tEveryone, except guests\n<\/label>\n<label class=\"radio modify_options\">\n\t<input name=\"who_can_post_in_shared_channels\" type=\"radio\" value=\"admin\" data-inline-saver=\"#prefs_shared_channels\" class=\"post_opt_radios\"\n\t{{#if_equal who_can_post_in_shared_channels.type compare=\"admin\"}}\n\tchecked=\"checked\"{{\/if_equal}}>\n\tOrg Admins\/Owners and Team Admins\/Owners\n<\/label>\n<label class=\"radio modify_options\">\n\t<input name=\"who_can_post_in_shared_channels\" type=\"radio\" value=\"owner\" data-inline-saver=\"#prefs_shared_channels\" class=\"post_opt_radios\"\n\t{{#if_equal who_can_post_in_shared_channels.type compare=\"owner\"}}\n\tchecked=\"checked\"{{\/if_equal}}>\n\tOrg Admins\/Owners\n<\/label>\n<label class=\"radio modify_options\">\n\t<input name=\"who_can_post_in_shared_channels\" type=\"radio\" value=\"multi\" data-inline-saver=\"#prefs_shared_channels\" class=\"post_opt_radios\" id=\"multi_post_selector\"\n\t{{#if_equal who_can_post_in_shared_channels.type compare=\"multi\"}}\n\tchecked=\"checked\"{{\/if_equal}}>\n\tOrg Admins\/Owners and selected members and user groups\n<\/label>\n";
