TS.raw_templates['enterprise_settings_team_settings_messaging_restrictions_policy'] = "<div class=\"org_policy_modal\">\n\t<h3 class=\"org_policy_modal__content_header lato_bold\">Require Messaging Restrictions<\/h3>\n\n\t<p class=\"org_policy_modal__content_subheader\">Set who can use @everyone and @channel, and who can post to your #general channel.<\/p>\n\n\t<form class=\"org_policy_modal__content_options org_policy_form\" data-form-policy=\"messaging_restrictions\">\n\t\t<label for=\"who_can_at_channel\" class=\"select small\">\n\t\t\tWho can use @channel and @here\n\t\t\t<select id=\"who_can_at_channel\" name=\"who_can_at_channel\" class=\"small\">\n\t\t\t\t<option value=\"ra\"{{#if_equal who_can_at_channel compare=\"ra\"}} selected{{\/if_equal}}>All team members, including guest accounts (default)<\/option>\n\t\t\t\t<option value=\"regular\"{{#if_equal who_can_at_channel compare=\"regular\"}} selected{{\/if_equal}}>Team members, excluding guest accounts<\/option>\n\t\t\t\t<option value=\"admin\"{{#if_equal who_can_at_channel compare=\"admin\"}} selected{{\/if_equal}}>Team Admins and Owners<\/option>\n\t\t\t\t<option value=\"owner\"{{#if_equal who_can_at_channel compare=\"owner\"}} selected{{\/if_equal}}>Team Owners<\/option>\n\t\t\t<\/select>\n\t\t<\/label>\n\n\t\t<label for=\"warn_before_at_channel\" class=\"select small\">\n\t\t\tShow a warning when using @channel or @everyone\n\t\t\t<select id=\"warn_before_at_channel\" name=\"warn_before_at_channel\" class=\"small\">\n\t\t\t\t<option value=\"always\"{{#if_equal warn_before_at_channel compare=\"always\"}} selected{{\/if_equal}}>Always (default)<\/option>\n\t\t\t\t<option value=\"daily\"{{#if_equal warn_before_at_channel compare=\"daily\"}} selected{{\/if_equal}}>The first time each day<\/option>\n\t\t\t\t<option value=\"once\"{{#if_equal warn_before_at_channel compare=\"once\"}} selected{{\/if_equal}}>Once<\/option>\n\t\t\t\t<option value=\"never\"{{#if_equal warn_before_at_channel compare=\"never\"}} selected{{\/if_equal}}>Never<\/option>\n\t\t\t<\/select>\n\t\t<\/label>\n\n\t\t<label for=\"who_can_post_general\" class=\"select small\">\n\t\t\tWho can post to the #general channel in each team\n\t\t\t<select id=\"who_can_post_general\" name=\"who_can_post_general\" class=\"small\">\n\t\t\t\t<option value=\"ra\"{{#if_equal who_can_post_general compare=\"ra\"}} selected{{\/if_equal}}>All team members, including guest accounts (default)<\/option>\n\t\t\t\t<option value=\"regular\"{{#if_equal who_can_post_general compare=\"regular\"}} selected{{\/if_equal}}>Team members, excluding guest accounts<\/option>\n\t\t\t\t<option value=\"admin\"{{#if_equal who_can_post_general compare=\"admin\"}} selected{{\/if_equal}}>Team Admins and Owners<\/option>\n\t\t\t\t<option value=\"owner\"{{#if_equal who_can_post_general compare=\"owner\"}} selected{{\/if_equal}}>Team Owners<\/option>\n\t\t\t<\/select>\n\t\t<\/label>\n\n\t\t<label for=\"who_can_at_everyone\" class=\"select small\">\n\t\t\tWho can use @everyone\n\t\t\t<select id=\"who_can_at_everyone\" name=\"who_can_at_everyone\" class=\"small\">\n\t\t\t\t<option value=\"ra\"{{#if_equal who_can_at_everyone compare=\"ra\"}} selected{{\/if_equal}}>All team members, including guest accounts<\/option>\n\t\t\t\t<option value=\"regular\"{{#if_equal who_can_at_everyone compare=\"regular\"}} selected{{\/if_equal}}{{#unless who_can_at_everyone}} selected{{\/unless}}>Team members, excluding guest accounts (default)<\/option>\n\t\t\t\t<option value=\"admin\"{{#if_equal who_can_at_everyone compare=\"admin\"}} selected{{\/if_equal}}>Team Admins and Owners<\/option>\n\t\t\t\t<option value=\"owner\"{{#if_equal who_can_at_everyone compare=\"owner\"}} selected{{\/if_equal}}>Team Owners<\/option>\n\t\t\t<\/select>\n\t\t<\/label>\n<\/div>\n\n<div class=\"hidden org_policy_modal_overlay_container\" data-modal-overlay data-enterprise-settings-confirm-policy-overlay>\n{{> enterprise_settings_confirm_policy}}\n<\/div>\n";
TS.raw_templates['enterprise_settings_team_settings_stats_policy'] = "<div class=\"org_policy_modal\">\n\t<h3 class=\"org_policy_modal__content_header lato_bold\">Require Team Stats Settings<\/h3>\n\n\t<p class=\"org_policy_modal__content_subheader\">By default, any team member may view the team stats pages. You can choose to disallow this.<\/p>\n\n\t<form class=\"org_policy_modal__content_options org_policy_form\" data-form-policy=\"stats\">\n\t\t<label for=\"stats_only_admins\" class=\"checkbox\">\n\t\t\t<input type=\"checkbox\" name=\"stats_only_admins\" value=\"1\" id=\"stats_only_admins\" {{#if stats_only_admins}}checked{{\/if}} \/>\n\t\t\tLimit viewing of team stats to Team Administrators\n\t\t<\/label>\n\t<\/form>\n\n<\/div>\n\n<div class=\"hidden org_policy_modal_overlay_container\" data-modal-overlay data-enterprise-settings-confirm-policy-overlay>\n{{> enterprise_settings_confirm_policy}}\n<\/div>\n";
TS.raw_templates['enterprise_settings_team_settings_user_groups_policy'] = "<div class=\"org_policy_modal\">\n\t<h3 class=\"org_policy_modal__content_header lato_bold\">User Groups<\/h3>\n\n\t<p class=\"org_policy_modal__content_subheader\">Set who can create and manage User Groups.<\/p>\n\n\t<form class=\"org_policy_modal__content_options org_policy_form\" data-form-policy=\"user_groups\">\n\n\t\t<label class=\"select large org_policy_modal__content_select eighteen_px_font large_bottom_margin\" for=\"who_can_create_delete_user_groups\">\n\t\t\tWho can create and disable User Groups\n\t\t\t<select name=\"who_can_create_delete_user_groups\" id=\"who_can_create_delete_user_groups\" class=\"large eighteen_px_font\" data-user-groups-select>\n\t\t\t\t<option value=\"regular\" {{#if_equal who_can_create_delete_user_groups compare=\"regular\"}}selected{{\/if_equal}}>All team members, but not guests<\/option>\n\t\t\t\t<option value=\"admin\" {{#if_equal who_can_create_delete_user_groups compare=\"admin\"}}selected{{\/if_equal}}>Team Admins and Owners only<\/option>\n\t\t\t\t<option value=\"owner\" {{#if_equal who_can_create_delete_user_groups compare=\"owner\"}}selected{{\/if_equal}}>Team Owners only<\/option>\n\t\t\t<\/select>\n\t\t<\/label>\n\n\t\t<label class=\"select large org_policy_modal__content_select eighteen_px_font large_bottom_margin\" for=\"who_can_edit_user_groups\">\n\t\t\tWho can edit User Groups\n\t\t\t<select name=\"who_can_edit_user_groups\" id=\"who_can_edit_user_groups\" class=\"large eighteen_px_font\" data-user-groups-select>\n\t\t\t\t<option value=\"regular\" {{#if_equal who_can_edit_user_groups compare=\"regular\"}}selected{{\/if_equal}}>All team members, but not guests<\/option>\n\t\t\t\t<option value=\"admin\" {{#if_equal who_can_edit_user_groups compare=\"admin\"}}selected{{\/if_equal}}{{#if_equal who_can_create_delete_user_groups compare=\"regular\"}}disabled aria-disabled=\"true\"{{\/if_equal}}>Team Admins and Owners only<\/option>\n\t\t\t\t<option value=\"owner\" {{#if_equal who_can_edit_user_groups compare=\"owner\"}}selected{{\/if_equal}} {{#or (if_equal who_can_create_delete_user_groups compare=\"admin\") (if_equal who_can_create_delete_user_groups compare=\"regular\")}}disabled aria-disabled=\"true\"{{\/or}}>Team Owners only<\/option>\n\t\t\t<\/select>\n\t\t<\/label>\n\n\n\t\t<label class=\"large\" for=\"auto_create_user_group_admin\">\n\t\t\t<input type=\"checkbox\" id=\"auto_create_user_group_admin\" name=\"auto_create_user_group_admin\" {{#if subteams_auto_create_admin}}checked=\"checked\" aria-checked=\"true\"{{\/if}} > Create a User Group for all admins\n\t\t<\/label>\n\n\t\t<label class=\"large large_bottom_margin\" for=\"auto_create_user_group_owner\">\n\t\t\t<input type=\"checkbox\" id=\"auto_create_user_group_owner\" name=\"auto_create_user_group_owner\" {{#if subteams_auto_create_owner}}checked=\"checked\" aria-checked=\"true\"{{\/if}} > Create a User Group for all owners\n\t\t<\/label>\n\t<\/form>\n\n<\/div>\n\n<div class=\"hidden org_policy_modal_overlay_container\" data-modal-overlay data-enterprise-settings-confirm-policy-overlay>\n{{> enterprise_settings_confirm_policy}}\n<\/div>\n";
TS.raw_templates['enterprise_settings_team_settings_message_editing_and_deleting_policy'] = "<div class=\"org_policy_modal\">\n\t<h3 class=\"org_policy_modal__content_header lato_bold\">Require Default Message Editing &amp; Deletion Settings<\/h3>\n\n\t<p class=\"org_policy_modal__content_subheader\">By default, messages can be edited or deleted at any time by the sender. You can choose to disallow editing or deleting of sent messages, or set an editing window.<\/p>\n\n\t<form class=\"org_policy_modal__content_options org_policy_form\" data-form-policy=\"messaging_editing_and_deleting\">\n\t\t<label for=\"msg_edit_window_mins\" class=\"select small\">\n\t\t\tAllow Editing\n\t\t\t<select id=\"msg_edit_window_mins\" name=\"msg_edit_window_mins\" class=\"small\">\n\t\t\t\t<option value=\"-1\" {{#if_equal msg_edit_window_mins compare=\"-1\"}}selected{{\/if_equal}}>Any time<\/option>\n\t\t\t\t<option value=\"0\" {{#if_equal msg_edit_window_mins compare=\"0\"}}selected{{\/if_equal}}>Never<\/option>\n\t\t\t\t<option value=\"1\" {{#if_equal msg_edit_window_mins compare=\"1\"}}selected{{\/if_equal}}>Up to 1 minute after posting<\/option>\n\t\t\t\t<option value=\"5\" {{#if_equal msg_edit_window_mins compare=\"5\"}}selected{{\/if_equal}}>Up to 5 minutes after posting<\/option>\n\t\t\t\t<option value=\"30\" {{#if_equal msg_edit_window_mins compare=\"30\"}}selected{{\/if_equal}}>Up to 30 minutes after posting<\/option>\n\t\t\t\t<option value=\"60\" {{#if_equal msg_edit_window_mins compare=\"60\"}}selected{{\/if_equal}}>Up to 1 hour after posting<\/option>\n\t\t\t\t<option value=\"1440\" {{#if_equal msg_edit_window_mins compare=\"1440\"}}selected{{\/if_equal}}>Up to 24 hours after posting<\/option>\n\t\t\t\t<option value=\"10080\" {{#if_equal msg_edit_window_mins compare=\"10080\"}}selected{{\/if_equal}}>Up to 1 week after posting<\/option>\n\t\t\t<\/select>\n\t\t<\/label>\n\n\t\t<p class=\"small_bottom_margin\">\n\t\t\tBy default, team members can delete their own messages, and administrators can delete messages from any sender.\n\t\t\tCheck this box to make message deletion admin-only.\n\t\t<\/p>\n\t\t<p>\n\t\t\t<label for=\"allow_message_deletion\" class=\"checkbox\">\n\t\t\t\t<input type=\"checkbox\" name=\"allow_message_deletion\" id=\"allow_message_deletion\" {{#unless allow_message_deletion}}checked{{\/unless}} \/>\n\t\t\t\tOnly administrators may delete messages\n\t\t\t<\/label>\n\t\t<\/p>\n\t<\/form>\n\t\t\n<\/div>\n\n<div class=\"hidden org_policy_modal_overlay_container\" data-modal-overlay data-enterprise-settings-confirm-policy-overlay>\n{{> enterprise_settings_confirm_policy}}\n<\/div>\n";
TS.raw_templates['enterprise_settings_team_settings_channel_management_restrictions_policy'] = "<div class=\"org_policy_modal\">\n\t<h3 class=\"org_policy_modal__content_header lato_bold\">Require Channel Management Restrictions Settings<\/h3>\n\n\t<p class=\"org_policy_modal__content_subheader\">Set who can create, archive, and kick from channels.<\/p>\n\n\t<form class=\"org_policy_modal__content_options org_policy_form\" data-form-policy=\"messaging_editing_and_deleting\">\n\n\t\t<label for=\"who_can_create_channels\" class=\"select small\">\n\t\t\tWho can create channels\n\t\t\t<select name=\"who_can_create_channels\" id=\"who_can_create_channels\" class=\"small\">\n\t\t\t\t<option value=\"regular\"{{#if_equal who_can_create_channels compare=\"regular\"}} selected=\"selected\"{{\/if_equal}}>All team members, but not guests<\/option>\n\t\t\t\t<option value=\"admin\"{{#if_equal who_can_create_channels compare=\"admin\"}} selected=\"selected\"{{\/if_equal}}>Team Admins and Owners only<\/option>\n\t\t\t\t<option value=\"owner\"{{#if_equal who_can_create_channels compare=\"owner\"}} selected=\"selected\"{{\/if_equal}}>Team Owners only<\/option>\n\t\t\t<\/select>\n\t\t<\/label>\n\t\t<label for=\"who_can_archive_channels\" class=\"select small\">\n\t\t\tWho can archive channels\n\t\t\t<select name=\"who_can_archive_channels\" id=\"who_can_archive_channels\" class=\"small\">\n\t\t\t\t<option value=\"regular\"{{#if_equal who_can_archive_channels compare=\"regular\"}} selected=\"selected\"{{\/if_equal}}>All team members, but not guests<\/option>\n\t\t\t\t<option value=\"admin\"{{#if_equal who_can_archive_channels compare=\"admin\"}} selected=\"selected\"{{\/if_equal}}>Team Admins and Owners only<\/option>\n\t\t\t\t<option value=\"owner\"{{#if_equal who_can_archive_channels compare=\"owner\"}} selected=\"selected\"{{\/if_equal}}>Team Owners only<\/option>\n\t\t\t<\/select>\n\t\t<\/label>\n\t\t<label for=\"who_can_create_groups\" class=\"select small\">\n\t\t\tWho can create private channels\n\t\t\t<select name=\"who_can_create_groups\" id=\"who_can_create_groups\" class=\"small\">\n\t\t\t\t<option value=\"ra\"{{#if_equal who_can_create_groups compare=\"ra\"}} selected=\"selected\"{{\/if_equal}}>All team members and guests<\/option>\n\t\t\t\t<option value=\"regular\"{{#if_equal who_can_create_groups compare=\"regular\"}} selected=\"selected\"{{\/if_equal}}>All team members, but not guests<\/option>\n\t\t\t\t<option value=\"admin\"{{#if_equal who_can_create_groups compare=\"admin\"}} selected=\"selected\"{{\/if_equal}}>Team Admins and Owners only<\/option>\n\t\t\t\t<option value=\"owner\"{{#if_equal who_can_create_groups compare=\"owner\"}} selected=\"selected\"{{\/if_equal}}>Team Owners only<\/option>\n\t\t\t<\/select>\n\t\t<\/label>\n\t\t<label for=\"who_can_kick_channels\" class=\"select small\">\n\t\t\tWho can kick other members from channels\n\t\t\t<select name=\"who_can_kick_channels\" id=\"who_can_kick_channels\" class=\"small\">\n\t\t\t\t<option value=\"regular\"{{#if_equal who_can_kick_channels compare=\"regular\"}} selected=\"selected\"{{\/if_equal}}>All team members, but not guests<\/option>\n\t\t\t\t<option value=\"admin\"{{#if_equal who_can_kick_channels compare=\"admin\"}} selected=\"selected\"{{\/if_equal}}{{#unless who_can_kick_channels}} selected=\"selected\"{{\/unless}}>Team Admins and Owners only<\/option>\n\t\t\t\t<option value=\"owner\"{{#if_equal who_can_kick_channels compare=\"owner\"}} selected=\"selected\"{{\/if_equal}}>Team Owners only<\/option>\n\t\t\t<\/select>\n\t\t<\/label>\n\t\t<label for=\"who_can_kick_groups\" class=\"select small\">\n\t\t\tWho can kick other members from private channels\n\t\t\t<select name=\"who_can_kick_groups\" id=\"who_can_kick_groups\" class=\"small\">\n\t\t\t\t<option value=\"regular\"{{#if_equal who_can_kick_groups compare=\"regular\"}} selected=\"selected\"{{\/if_equal}}>All team members, but not guests<\/option>\n\t\t\t\t<option value=\"admin\"{{#if_equal who_can_kick_groups compare=\"admin\"}} selected=\"selected\"{{\/if_equal}}>Team Admins and Owners only<\/option>\n\t\t\t\t<option value=\"owner\"{{#if_equal who_can_kick_groups compare=\"owner\"}} selected=\"selected\"{{\/if_equal}}>Team Owners only<\/option>\n\t\t\t<\/select>\n\t\t<\/label>\n\n\t<\/form>\n\n<\/div>\n\n<div class=\"hidden org_policy_modal_overlay_container\" data-modal-overlay data-enterprise-settings-confirm-policy-overlay>\n{{> enterprise_settings_confirm_policy}}\n<\/div>\n";
TS.raw_templates['enterprise_settings_team_settings_slackbot_response_policy'] = "<div class=\"org_policy_modal\">\n\t<h3 class=\"org_policy_modal__content_header lato_bold\">Slackbot Responses Policy<\/h3>\n\n\t<p class=\"org_policy_modal__content_subheader\">By default, any team member may add Slackbot responses for your team. You can choose to disallow this.<\/p>\n\n\t<form class=\"org_policy_modal__content_options org_policy_form\" data-form-policy=\"slackbot_responses\">\n\t\t\t<label for=\"slackbot_responses_disabled\" class=\"checkbox\">\n\t\t\t\t<input\n\t\t\t\t\ttype=\"checkbox\"\n\t\t\t\t\tname=\"slackbot_responses_disabled\"\n\t\t\t\t\tvalue=\"1\"\n\t\t\t\t\tid=\"slackbot_responses_disabled\"\n\t\t\t\t\t{{#if slackbot_responses_disabled}}checked aria-checked=\"true\"{{\/if}}>\n\t\t\t\tDisable Slackbot responses\n\t\t\t<\/label>\n\n\t\t\t<label for=\"slackbot_responses_only_admins\" class=\"checkbox\">\n\t\t\t\t<input\n\t\t\t\t\ttype=\"checkbox\"\n\t\t\t\t\tname=\"slackbot_responses_only_admins\"\n\t\t\t\t\tvalue=\"1\"\n\t\t\t\t\tid=\"slackbot_responses_only_admins\"\n\t\t\t\t\t{{#if slackbot_responses_only_admins}}checked aria-checked=\"true\"{{\/if}}>\n\t\t\t\tOnly admins may add Slackbot responses\n\t\t\t<\/label>\n\t<\/form>\n<\/div>\n\n<div\n\tclass=\"hidden org_policy_modal_overlay_container\"\n\tdata-modal-overlay\n\tdata-enterprise-settings-confirm-policy-overlay>\n\t{{> enterprise_settings_confirm_policy}}\n<\/div>\n";
TS.raw_templates['enterprise_settings_team_settings_public_file_policy'] = "<div class=\"org_policy_modal\">\n\t<h3 class=\"org_policy_modal__content_header lato_bold\">Public File Sharing Policy<\/h3>\n\n\t<p class=\"org_policy_modal__content_subheader\">By default, team members can generate shareable URLs for files that allow people not on your team to view a file uploaded to your team. You can choose to disallow this.<\/p>\n\n\t<form class=\"org_policy_modal__content_options org_policy_form\" data-form-policy=\"public_file_sharing\">\n\t\t\t<label for=\"public_file_sharing\" class=\"checkbox\">\n\t\t\t\t<input\n\t\t\t\t\ttype=\"checkbox\"\n\t\t\t\t\tname=\"public_file_sharing\"\n\t\t\t\t\tvalue=\"1\"\n\t\t\t\t\tid=\"public_file_sharing\"\n\t\t\t\t\t{{#if disallow_public_file_urls}}checked aria-checked=\"true\"{{\/if}}>\n\t\t\t\tDisable public file URL creation\n\t\t\t<\/label>\n\t<\/form>\n<\/div>\n\n<div\n\tclass=\"hidden org_policy_modal_overlay_container\"\n\tdata-modal-overlay\n\tdata-enterprise-settings-confirm-policy-overlay>\n\t{{> enterprise_settings_confirm_policy}}\n<\/div>\n";
TS.raw_templates['enterprise_settings_team_settings_customization_policy'] = "<div class=\"org_policy_modal\">\n\t<h3 class=\"org_policy_modal__content_header lato_bold\">Customization Policy<\/h3>\n\n\t<p class=\"org_policy_modal__content_subheader\">By default, any full team member (but not guests) may add custom emoji, custom loading messages and custom chat sounds for your team. You can make these settings admin-only.<\/p>\n\n\t<form class=\"org_policy_modal__content_options org_policy_form\" data-form-policy=\"customization\">\n\t\t\t<label for=\"emoji_only_admins\" class=\"checkbox\">\n\t\t\t\t<input\n\t\t\t\t\ttype=\"checkbox\"\n\t\t\t\t\tname=\"emoji_only_admins\"\n\t\t\t\t\tvalue=\"1\"\n\t\t\t\t\tid=\"emoji_only_admins\"\n\t\t\t\t\t{{#if emoji_only_admins}}checked aria-checked=\"true\"{{\/if}}>\n\t\t\t\tOnly Team Owners and Admins may create custom emoji\n\t\t\t<\/label>\n\t\t\t<label for=\"loading_only_admins\" class=\"checkbox\">\n\t\t\t\t<input\n\t\t\t\t\ttype=\"checkbox\"\n\t\t\t\t\tname=\"loading_only_admins\"\n\t\t\t\t\tvalue=\"1\"\n\t\t\t\t\tid=\"loading_only_admins\"\n\t\t\t\t\t{{#if loading_only_admins}}checked aria-checked=\"true\"{{\/if}}>\n\t\t\t\tOnly Team Owners and Admins may set custom loading messages\n\t\t\t<\/label>\n\t<\/form>\n<\/div>\n\n<div\n\tclass=\"hidden org_policy_modal_overlay_container\"\n\tdata-modal-overlay\n\tdata-enterprise-settings-confirm-policy-overlay>\n\t{{> enterprise_settings_confirm_policy}}\n<\/div>\n";
TS.raw_templates['enterprise_settings_team_settings_gateways_policy'] = "<div class=\"org_policy_modal\">\n\t<h3 class=\"org_policy_modal__content_header lato_bold\">Gateways Policy<\/h3>\n\n\t<p class=\"org_policy_modal__content_subheader\">Slack allows you to open gateways so users on XMPP or IRC can connect to Slack via those protocols using their existing clients.<\/p>\n\n\t<form class=\"org_policy_modal__content_options org_policy_form\" data-form-policy=\"gateways\">\n\t\t\t<label for=\"gateway_allow_xmpp_ssl\" class=\"checkbox\">\n\t\t\t\t<input type=\"checkbox\" name=\"gateway_allow_xmpp_ssl\" value=\"1\" id=\"gateway_allow_xmpp_ssl\" {{#if gateway_allow_xmpp_ssl}}checked aria-checked=\"true\"{{\/if}} \/>\n\t\t\t\tEnable XMPP gateway (SSL only)\n\t\t\t<\/label>\n\t\t\t<label for=\"gateway_allow_irc_ssl\" class=\"checkbox\">\n\t\t\t\t<input type=\"checkbox\" name=\"gateway_allow_irc_ssl\" value=\"1\" id=\"gateway_allow_irc_ssl\" {{#if gateway_allow_irc_ssl}}checked aria-checked=\"true\"{{\/if}} \/>\n\t\t\t\tEnable IRC gateway (SSL only)\n\t\t\t<\/label>\n\t\t\t<label for=\"gateway_allow_irc_plain\" id=\"gateway_allow_irc_plain_label\" class=\"checkbox{{#unless gateway_allow_irc_ssl}} disabled{{\/unless}}\">\n\t\t\t\t<input type=\"checkbox\" name=\"gateway_allow_irc_plain\" value=\"1\" id=\"gateway_allow_irc_plain\" {{#if gateway_allow_irc_plain}}checked{{\/if}} {{#unless gateway_allow_irc_ssl}}disabled=\"disabled\" aria-disabled=\"true\"{{\/unless}} class=\"small_left_margin\" \/>\n\t\t\t\tAllow non-SSL IRC connections (plaintext)\n\t\t\t<\/label>\n\t<\/form>\n<\/div>\n\n<div\n\tclass=\"hidden org_policy_modal_overlay_container\"\n\tdata-modal-overlay\n\tdata-enterprise-settings-confirm-policy-overlay>\n\t{{> enterprise_settings_confirm_policy}}\n<\/div>\n";
TS.raw_templates['enterprise_settings_remove_policy'] = "<div class=\"org_policy_modal\">\n\t<h3 class=\"org_policy_modal__content_header no_margin\">Remove the Policy for<\/h3>\n\t<h3 class=\"org_policy_modal__content_header no_margin\">{{policy_display_name}}?<\/h3>\n\n\t<p class=\"org_policy_modal__description\">Once you remove this policy, teams will be able<br>to edit the setting again. Removing the policy<br>won\u2019t change anything else for teams.<\/p>\n<\/div>";
TS.raw_templates['enterprise_settings_confirm_policy'] = "<div class=\"org_policy_modal_overlay\">\n\n\t<div class=\"org_policy_modal_overlay__content\">\n\t\t<h3 class=\"org_policy_modal_overlay__content_header\">Create a Policy for<\/h3>\n\t\t<h3 class=\"org_policy_modal_overlay__content_header\"><span class=\"overlay_display_name\"><\/span>?<\/h3>\n\n\t\t<p class=\"org_policy_modal_overlay__description\">Saving this configuration as an organization policy will automatically change the setting on all teams in your organization. They will not be able to change the configuration.<\/p>\n\n\t\t<form class=\"org_policy_modal_overlay__modal_warning_pref_form\">\n\t\t\t<label><input data-modal-warning-pref type=\"checkbox\" name=\"modal_warning_pref\" value=\"true\"><p class=\"org_policy_modal_overlay__modal_warning_pref\">Don\u2019t show this warning again.<\/p><\/label>\n\t\t<\/form>\n\n\t\t<div data-confirm-buttons><\/div>\n\t<\/div>\n<\/div>";
TS.raw_templates['enterprise_settings_confirm_policy_message_retention'] = "<div class=\"org_policy_modal_overlay\">\n\n\t<div class=\"org_policy_modal_overlay__content\">\n\t\t<div data-confirmation-message-sensitive class=\"hidden\">\n\t\t\t<h3 class=\"org_policy_modal_overlay__content_header\">Are you sure you only want to retain messages for a limited amount of time?<\/h3>\n\t\t\t<p class=\"org_policy_modal_overlay__description\">Setting a custom duration for message retention means that messages in your team's archives will be deleted after the number of days you set:<\/p>\n\n\t\t\t<p class=\"bold hidden\" data-retention-duration-wrapper>Channels: <span data-retention-duration=\"retention\"><\/span> days<\/p>\n\t\t\t<p class=\"bold hidden\" data-retention-duration-wrapper>Private Channels: <span data-retention-duration=\"group_retention\"><\/span> days<\/p>\n\t\t\t<p class=\"bold hidden\" data-retention-duration-wrapper>Direct Messages: <span data-retention-duration=\"dm_retention\"><\/span> days<\/p>\n\n\t\t\t<p>Deletion is permanent, so you won't be able to retrieve messages older than the time period you set. Proceed with caution!<\/p>\n\t\t<\/div>\n\t\t<div data-confirmation-message class=\"hidden\">\n\t\t\t<h3 class=\"org_policy_modal_overlay__content_header\">Create a Policy for<\/h3>\n\t\t<h3 class=\"org_policy_modal_overlay__content_header\"><span class=\"overlay_display_name\"><\/span>?<\/h3>\n\n\t\t<p class=\"org_policy_modal_overlay__description\">Saving this configuration as an organization policy will automatically change the setting on all teams in your organization. They will not be able to change the configuration.<\/p>\n\t\t<\/div>\n\n\t\t<form class=\"org_policy_modal_overlay__modal_warning_pref_form\">\n\t\t\t<label><input data-modal-warning-pref type=\"checkbox\" name=\"modal_warning_pref\" value=\"true\"><p class=\"org_policy_modal_overlay__modal_warning_pref\">Don\u2019t show this warning again.<\/p><\/label>\n\t\t<\/form>\n\n\t\t<div data-confirm-buttons><\/div>\n\t<\/div>\n<\/div>";
TS.raw_templates['enterprise_settings_confirm_policy_file_retention'] = "<div class=\"org_policy_modal_overlay\">\n\n\t<div class=\"org_policy_modal_overlay__content\">\n\t\t<div data-confirmation-message-sensitive class=\"hidden\">\n\t\t\t<h3 class=\"org_policy_modal_overlay__content_header\">Are you sure you only want to retain files for <span data-retention-duration=\"files_retention\"><\/span>?<\/h3>\n\t\t\t<p class=\"org_policy_modal_overlay__description\">Setting a custom duration for file retention means that files in your team's archives will be deleted after the number of days you set.<\/p>\n\n\t\t\t<p>Deletion is permanent, so you won't be able to retrieve files older than the time period you set. Proceed with caution!<\/p>\n\t\t<\/div>\n\t\t<div data-confirmation-message class=\"hidden\">\n\t\t\t<h3 class=\"org_policy_modal_overlay__content_header\">Create a Policy for<\/h3>\n\t\t<h3 class=\"org_policy_modal_overlay__content_header\"><span class=\"overlay_display_name\"><\/span>?<\/h3>\n\n\t\t<p class=\"org_policy_modal_overlay__description\">Saving this configuration as an organization policy will automatically change the setting on all teams in your organization. They will not be able to change the configuration.<\/p>\n\t\t<\/div>\n\n\t\t<form class=\"org_policy_modal_overlay__modal_warning_pref_form\">\n\t\t\t<label><input data-modal-warning-pref type=\"checkbox\" name=\"modal_warning_pref\" value=\"true\"><p class=\"org_policy_modal_overlay__modal_warning_pref\">Don\u2019t show this warning again.<\/p><\/label>\n\t\t<\/form>\n\n\t\t<div data-confirm-buttons><\/div>\n\t<\/div>\n<\/div>";
