TS.raw_templates['apps_restrict_app_modal_body'] = "{{#if is_api_tester}}\n\t<p class=\"no_margin\">\n\t\tWe'll send {{#if user_name}}<b>{{user_name}}<\/b>{{else}}this user{{\/if}}\n\t\ta message that you've denied their request.\n\t<\/p>\n{{else if is_custom_integration}}\n\t<p class=\"no_margin\">\n\t\t{{#if user_name}}<b>{{user_name}}<\/b>{{else}}This user{{\/if}}\n\t\twill not be able to add a configuration for\n\t\t{{#if app_name}}{{app_name}}{{else}}this app{{\/if}}.\n\t<\/p>\n{{else}}\n\t<p>\n\t\tTeam members will\n\t\t{{#if request_id}}not{{else}}no longer{{\/if}}\n\t\tbe able to add or edit {{app_name}} configurations.\n\t<\/p>\n\t<p class=\"no_margin\">\n\t\t{{#if config_count}}\n\t\t\t<b>{{config_count}} existing {{pluralize config_count 'configuration'}}<\/b> will remain in use until removed from Team Apps.\n\t\t{{else}}\n\t\t\tThere are no are no existing configurations.\n\t\t{{\/if}}\n\t<\/p>\n{{\/if}}\n";
