TS.raw_templates['import_user_mapping_row'] = "<div class=\"col span_3_of_6 break_all\">{{user.name}} ({{#if user.import_email}}{{user.import_email}}{{else}}no email{{\/if}})<\/div>\n<div id=\"user_{{user.id}}_options\" class=\"user_map_selector col span_3_of_6\">\n\t<label class=\"select small\">\n\t\t<select name=\"user_{{user.id}}\" class=\"small user_change\">\n\t\t\t<option value=\"-\" {{#if_equal user.mapping compare=\"-\"}}SELECTED{{\/if_equal}}>Choose one:<\/option>\n\t\t\t<option value=\"invite\" {{#if_equal user.mapping compare=\"invite\"}}SELECTED{{\/if_equal}}>Create & invite user<\/option>\n\t\t\t<option value=\"create\" {{#if_equal user.mapping compare=\"create\"}}SELECTED{{\/if_equal}}>Create a disabled user, without inviting them<\/option>\n\t\t\t<option value=\"bot\" {{#if_equal user.mapping compare=\"bot\"}}SELECTED{{\/if_equal}}>Do not create a user, just import messages<\/option>\n\t\t\t<option value=\"0\" {{#if_equal user.mapping compare=\"0\"}}SELECTED{{\/if_equal}}>Skip user completely, importing no messages<\/option>\n\t\t\t<optgroup label=\"Existing Users\">\n\t\t\t\t{{#foreach existing_users}}\n\t\t\t\t\t<option value=\"{{this.value.id}}\" {{#if_equal this.value.id compare=..\/user.mapping}}SELECTED{{\/if_equal}}>{{getMemberDisplayNameById this.value.id}}<\/option>\n\t\t\t\t{{\/foreach}}\n\t\t\t<\/optgroup>\n\t\t<\/select>\n\t<\/label>\n\t<div class=\"control-group{{#if user.email_ok}}{{else}} error{{\/if}} small_top_margin\">\n\t\t<span class=\"small_left_margin help-inline dup-email hidden\">\n\t\t\t<i class=\"ts_icon ts_icon_warning moscow_red\"><\/i> Another user has this email.\n\t\t<\/span>\n\t\t<span class=\"small_left_margin help-inline req-email {{#if user.email_ok}} hidden{{\/if}}\">\n\t\t\t<i class=\"ts_icon ts_icon_warning moscow_red\"><\/i> An email address is required.\n\t\t<\/span>\n\t\t<span class=\"small_left_margin help-inline invalid-email hidden\">\n\t\t\t<i class=\"ts_icon ts_icon_warning moscow_red\"><\/i> This address is invalid.\n\t\t<\/span>\n\t\t<input type=\"email\" name=\"email_{{user.id}}\" value=\"{{user.email}}\" placeholder=\"email@domain.com\" class=\"small {{#if user.show_input}}{{else}}hidden{{\/if}}\">\n\t<\/div>\n<\/div>";
TS.raw_templates['import_channel_mapping_row'] = "<div class=\"col span_3_of_6 break_all\">{{channel.name}}<\/div>\n<div class=\"channel_map_selector col span_3_of_6\">\n\t<label class=\"select small\">\n\t\t<select name=\"channel_{{channel.id}}\" class=\"small\">\n\t\t\t<option value=\"create\" {{#if_equal channel.mapping compare=\"create\"}}SELECTED{{\/if_equal}}>Create new public channel<\/option>\n\t\t\t<option value=\"create_group\" {{#if_equal channel.mapping compare=\"create_group\"}}SELECTED{{\/if_equal}}>Create new private channel<\/option>\n\t\t\t<option value=\"0\" {{#if_equal channel.mapping compare=\"0\"}}SELECTED{{\/if_equal}}>Skip channel (don\u2019t import)<\/option>\n\t\t\t<optgroup label=\"Add to existing channel\">\n\t\t\t\t{{#foreach existing_channels}}\n\t\t\t\t\t<option value=\"{{this.value.id}}\" {{#if_equal this.value.id compare=..\/channel.mapping}}SELECTED{{\/if_equal}}>#{{this.value.name}}<\/option>\n\t\t\t\t{{\/foreach}}\n\t\t\t<\/optgroup>\n\t\t<\/select>\n\t<\/label>\n<\/div>";
